import React, { Component } from 'react'
import { Card, CardHeader, CardFooter, CardBody,
         CardTitle, CardText } from 'reactstrap';
import { Col } from 'reactstrap';
import { Collapse } from 'reactstrap';

import 'bootstrap/dist/css/bootstrap.min.css'

export default class StdCard extends Component {

   state = {
      collapse: true
   }

   toggle = () => {
      this.setState(state => ({ collapse: !state.collapse }));
   }

   render() {

      const { headerText, bodyText, cardText } = this.props;

      return (
         <Col md="4" sm="6" xs="12">
            <Card color="dark" className="text-white mt-4">
               <CardHeader>
                  {headerText}
                  <span class="fa fa-angle-down float-right header-icon text-secondary" onClick={this.toggle}></span>
               </CardHeader>
               <Collapse isOpen={this.state.collapse}>
                  <CardBody>
                     {bodyText}
                     <CardText>
                        {cardText}
                     </CardText>
                  </CardBody>
               </Collapse>
            </Card>
         </Col>
      )
   }
}
