import React, { Component } from 'react';
import { Row, Col } from 'reactstrap';
import Sidebar from '../../components/Sidebar/Sidebar';
import SidebarItem from '../../components/Sidebar/SidebarItem';
import CreateuserForm from './components/CreateUserForm';
import Client from '../../Modules/Client/Client';

export default class AdminPanel extends Component {
   state = {
      selected: 1,
      toasts: [],
      username: '',
      email: '',
      password: '',
      role: ''
   };

   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };

   handleSubmit = async e => {
      e.preventDefault();

      const { email, username, role, password } = this.state;

      let usr = Client.Services.UsersService.createUserDTO(
         username,
         email,
         password,
         role,
         '5dfffe289fe19f21c85f3287', //<- temporaryfix
         '5dfffe289fe19f21c85f3287' //<- temporaryfix
      );
      try {
         let result = await Client.Services.UsersService.saveNewUser(usr);
         if (result.status === 200) {
            Client.Notifications.showSuccessNotification(
               `User ${username} successfully saved`
            );
         }
      } catch (err) {
         if (err.response.status === 409) {
            Client.Notifications.showAlertNotification(
               `Error: ${err.response.data.message}`
            );
         }
         if (err.response.status !== 409) {
            Client.Notifications.showAlertNotification(
               `Error while saving ${username} user`
            );
         }
      }
   };

   render() {
      const { selected, email, username, role, password } = this.state;
      return (
         <div>
            <Sidebar>
               <SidebarItem icon='user' selected={selected === 1} />
            </Sidebar>
            <Row>
               <Col sx='12' sm='6'>
                  <CreateuserForm
                     email={email}
                     username={username}
                     password={password}
                     role={role}
                     onChange={this.onChange}
                     onClick={this.handleSubmit}
                  />
               </Col>
            </Row>
         </div>
      );
   }
}
