import React, { Component } from 'react';
import {
   Table,
   Button,
   ButtonGroup,
   Modal,
   Card,
   CardHeader,
   CardBody,
   Spinner
} from 'reactstrap';
import UpdateProjectForm from '../../Forms/ProjectForms/UpdateProjectForm/UpdateProjectForm';
import axios from 'axios';
import Auth from '../../../Modules/Auth/Auth';

export default class ProjectTable extends Component {
   constructor(props) {
      super(props);
      this.getProjects = this.getProjects.bind(this);
      this.renderTableHeaders = this.renderTableHeaders.bind(this);
      this.renderTableData = this.renderTableData.bind(this);
      this.handleClick = this.handleClick.bind(this);
      this.closeModal = this.closeModal.bind(this);
      this.state = {
         modal: false,
         theme: this.props.theme,
         isLoading: true,
         data: this.props.data,
         filterName: this.props.name,
         id: '',
         name: '',
         status: '',
         deliveryType: '',
         sm: '',
         ops: '',
         cim: 0,
         ras: 0,
         projects: []
      };
   }

   componentDidMount() {
      this.getProjects();
   }

   getProjects() {
      this.setState({ isLoading: true });

      const URL = '/api/projects';

      axios
         .get(URL)
         .then(res => {
            this.setState({
               projects: res.data,
               isLoading: false
            });
         })
         .catch(err => {
            alert('Error loading Projects table');
            console.log(JSON.stringify(err));
         });
   }

   setTheme() {
      const { theme } = this.state;
      if (theme === 'dark') {
         return 'table-dark';
      }
      if (theme === 'light') {
         return '';
      }
      console.log(theme);
   }

   renderTableHeaders() {
      return (
         <tr className='bg-black text-white'>
            <th>Name</th>
            <th>Status</th>
            <th>Delivery Type</th>
            <th>SM</th>
            <th>OPS</th>
            <th className='text-center'>CIM</th>
            <th className='text-center'>RAS</th>
            <th className='text-center'>Action</th>
         </tr>
      );
   }

   addActionButton(project) {
      const { edit, remove, retire } = this.props;

      if (remove && retire && edit)
         return (
            <ButtonGroup>
               <Button
                  size='sm'
                  color='danger'
                  disabled={!Auth.isAuthorizedToRemove()}
                  onClick={e => this.handleClick(e, 'remove', project)}
               >
                  <span className='fas fa-trash-alt' />
               </Button>
               <Button
                  size='sm'
                  color='danger'
                  disabled={!Auth.isAuthorizedToRemove()}
                  onClick={e => this.handleClick(e, 'retire', project)}
               >
                  <span className='fas fa-times-circle' />
               </Button>
               <Button
                  size='sm'
                  disabled={!Auth.isAuthorizedToRemove()}
                  onClick={e => this.handleClick(e, 'edit', project)}
               >
                  <span className='fas fa-edit' />
               </Button>
            </ButtonGroup>
         );

      if (remove && retire)
         return (
            <ButtonGroup>
               <Button
                  size='sm'
                  color='danger'
                  disabled={!Auth.isAuthorizedToRemove()}
                  onClick={e => this.handleClick(e, 'remove', project)}
               >
                  <span className='fas fa-trash-alt' />
               </Button>
               <Button
                  size='sm'
                  color='danger'
                  disabled={!Auth.isAuthorizedToRemove()}
                  onClick={e => this.handleClick(e, 'retire', project)}
               >
                  <span className='fas fa-times-circle' />
               </Button>
            </ButtonGroup>
         );

      if (edit)
         return (
            <Button
               size='sm'
               disabled={!Auth.isAuthorizedToRemove()}
               onClick={e => this.handleClick(e, 'edit', project)}
            >
               <span className='fas fa-edit' />
            </Button>
         );

      if (remove)
         return (
            <Button
               size='sm'
               disabled={!Auth.isAuthorizedToRemove()}
               onClick={e => this.handleClick(e, 'remove', project)}
            >
               <span className='fas fa-trash-alt' />
            </Button>
         );
   }

   handleClick(e, action, project) {
      e.preventDefault();
      if (action === 'edit') this.editProject(project);
      if (action === 'remove') this.removeProject(project);
      if (action === 'retire') this.retireProject(project);
   }

   editProject(project) {
      this.setState(prevState => ({
         modal: !prevState.modal
      }));
      this.setState({
         id: project._id,
         name: project.name,
         status: project.status.status,
         deliveryType: project.deliveryType.deliveryType,
         sm: project.sm.name,
         ops: project.ops.name,
         cim: project.cim,
         ras: project.ras
      });
   }

   closeModal() {
      this.setState(prevState => ({
         modal: !prevState.modal
      }));
   }

   removeProject(project) {
      const URL = '/api/projects/' + project._id;

      axios
         .delete(URL)
         .then(res => {
            alert('Project ' + project.name + ' removed!');
            this.getProjects();
         })
         .catch(err => {
            alert('Something went wrong :(');
         });
   }

   retireProject(project) {
      const URL = '/api/projects/retire/' + project._id;

      axios
         .put(URL)
         .then(res => {
            alert('Project ' + project.name + ' is retired');
            this.getProjects();
         })
         .catch(err => {
            alert('Something went wrong :(');
         });
   }

   renderTableData() {
      return this.state.projects.map((project, index) => {
         return (
            <tr key={project._id}>
               <td>{project.name}</td>
               <td>{project.status.status}</td>
               <td>{project.deliveryType.deliveryType}</td>
               <td>{project.sm.name}</td>
               <td>{project.ops.name}</td>
               <td className='text-center'>{project.cim}</td>
               <td className='text-center'>{project.ras}</td>
               <td className='text-center'>{this.addActionButton(project)}</td>
            </tr>
         );
      });
   }

   setCardTheme() {
      const { theme } = this.state;
      if (theme === 'dark') {
         return 'bg-dark text-white';
      }
      if (theme === 'light') {
         return '';
      }
   }

   render() {
      const {
         theme,
         isLoading,
         modal,
         id,
         name,
         status,
         deliveryType,
         sm,
         ops,
         cim,
         ras
      } = this.state;

      if (isLoading) {
         return (
            <div className='text-center'>
               <Spinner color='primary' />
            </div>
         );
      }

      return (
         <div>
            <p>
               All Available Projects
               <Button
                  color='primary'
                  size='sm'
                  className='float-right'
                  onClick={this.getProjects}
               >
                  <span className='fas fa-sync-alt' />
               </Button>
            </p>
            <hr />
            <Table
               responsive
               borderless
               striped
               hover
               size='sm'
               className={this.setTheme()}
            >
               <thead>{this.renderTableHeaders()}</thead>
               <tbody>{this.renderTableData()}</tbody>
            </Table>
            <Modal isOpen={modal} toggle className='modal-full modal-my'>
               <Card className={this.setCardTheme()}>
                  <CardHeader>
                     <span class='fas fa-chart-line header-icon' />
                     {'Update project: ' + name}
                     <span
                        className='far fa-window-restore float-right header-icon text-secondary'
                        onClick={this.closeModal}
                     />
                  </CardHeader>

                  <CardBody className='card-body'>
                     <UpdateProjectForm
                        theme={theme}
                        projectID={id}
                        projectName={name}
                        sm={sm}
                        status={status}
                        deliveryType={deliveryType}
                        ops={ops}
                        cim={cim}
                        ras={ras}
                     />
                  </CardBody>
               </Card>
            </Modal>
         </div>
      );
   }
}

