import React, { Component } from 'react';
import { Row, Col } from 'reactstrap';
import CreateuserForm from './components/CreateUserForm';
import Client from '../../Modules/Client/Client';

export default class AdminPanel extends Component {
   state = {
      toasts: [],
      username: '',
      email: '',
      password: '',
      role: 'USER'
   };

   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };
   handleSubmit = async e => {
      const { email, username, role, password } = this.state;
      e.preventDefault();
      //FIXME: service is not working
      try {
         let usr = Client.Services.UsersService.createUserDTO(
            username,
            email,
            password,
            role,
            '5dfffe289fe19f21c85f3287',
            '5dfffe289fe19f21c85f3287'
         );
         Client.Services.UsersService.saveNewUser(usr);
         Client.Notifications.showSuccessNotification(
            `New user ${username} was successfully created`
         );
      } catch (err) {
         this.setState({ message: 'Something went wrong' });
      }
   };

   render() {
      const { email, username, role, password } = this.state;
      return (
         <div>
            <Row>
               <Col sx='12' sm='6'>
                  <CreateuserForm
                     email={email}
                     username={username}
                     password={password}
                     role={role}
                     onChange={this.onChange}
                     onClick={this.handleSubmit}
                  />
               </Col>
            </Row>
         </div>
      );
   }
}
