import React, { Component } from 'react'
import { Collapse } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css'
import '@fortawesome/fontawesome-free/css/all.min.css'

import LineChart from '../Charts/LineChart/LineChart';

export default class CardWithChart extends Component {

   constructor() {
      super(props);
      this.toggle = this.toggle.bind(this);
      this.state = { collapse: false };
   }

   toggle() {
      this.setState(state => ({ collapse: !state.collapse }));
   }

   render() {

      const { headerText, bodyText, cardText } = this.props;

      return (
         <div className="col-sm-6 col-xs-6 mt-4">
            <div className="card bg-dark text-white">
               <div className="card-header">
                  <span class="fas fa-chart-line header-icon"></span>
                  {headerText}
                  <span class="fa fa-angle-down float-right header-icon text-secondary" onClick={this.toggle}></span>
                  <span className="fa fa-toggle-on float-right header-icon text-secondary"></span>
               </div>
               <Collapse>
               <div className="card-body">
                  <LineChart></LineChart>
                  <p class="card-text">
                     {cardText}
                  </p>
               </div>
               </Collapse>
            </div>
         </div>
      )
   }
}
