import React, { Component } from 'react';
import { Button, Tab, Tabs } from '@blueprintjs/core';
import MainContainer from '../MainContainer/MainContainer';
//import '../NavBar/Navbar.css'
import './Sidebar.css';

export default class Sidebar extends Component {
   state = {
      selected: 0
   };

   changeSelection(id) {
      this.setState({ selected: id });
   }

   render() {
      return (
         <div>
            <nav class='side-bar no-border'>
               <Button
                  minimal
                  large
                  icon='home'
                  fill
                  active={this.state.selected === 1}
                  intent={this.state.selected === 1 ? 'primary' : null}
                  onClick={() => this.changeSelection(1)}
               ></Button>
               <Button
                  minimal
                  large
                  icon='dashboard'
                  fill
                  active={this.state.selected === 2}
                  intent={this.state.selected === 2 ? 'primary' : null}
                  onClick={() => this.changeSelection(2)}
               ></Button>
               <Button
                  minimal
                  large
                  icon='control'
                  fill
                  active={this.state.selected === 3}
                  intent={this.state.selected === 3 ? 'primary' : null}
                  onClick={() => this.changeSelection(3)}
               ></Button>
               <Button
                  minimal
                  large
                  icon='edit'
                  fill
                  active={this.state.selected === 4}
                  intent={this.state.selected === 4 ? 'primary' : null}
                  onClick={() => this.changeSelection(4)}
               ></Button>
               <Button
                  minimal
                  large
                  icon='locate'
                  fill
                  active={this.state.selected === 5}
                  intent={this.state.selected === 5 ? 'primary' : null}
                  onClick={() => this.changeSelection(5)}
               ></Button>
            </nav>

            <MainContainer>{this.state.selected}</MainContainer>
         </div>
      );
   }
}
