import React, { Component } from 'react';
import {
   Row, Col, Card, CardBody, Button, Form, FormGroup, Input,
   InputGroup, InputGroupAddon, InputGroupText
} from 'reactstrap';
import Auth from '../Modules/Auth/Auth'

import '../App.css';
import './Login.css'
import 'bootstrap/dist/css/bootstrap.css'
import '@fortawesome/fontawesome-free/css/all.css'


export default class Login extends Component {

   state = {
      isLoading : false,
      email : "",
      password: ""
   }

   handleSubmit = async event => {
      event.preventDefault();
      try {
        await Auth.signIn(this.state.email, this.state.password);
        alert("Logged in");
      } catch (e) {
        alert(e.message);
      }
    }
    

   render() {
      return (
         <div className="Login" >
            <div className="container-fluid align-middle">
               <Row className="justify-content-center">
                  <Col xl="4" lg="4" md="4" sm="12" xs="12" className="mt-4">
                     <Card inverse color="dark">
                        <CardBody>
                           <Form>
                              <span className="fas fa-dice-d20 float-right" />
                              <p className="text-center">Sign in</p>
                              <FormGroup>
                                 <InputGroup size="sm">
                                    <InputGroupAddon addonType="prepend">
                                       <InputGroupText>
                                          <i className="fas fa-user"></i>
                                       </InputGroupText>
                                    </InputGroupAddon>
                                    <Input type="email" name="email" id="exampleEmail" placeholder="email" />
                                 </InputGroup>
                              </FormGroup>
                              <FormGroup>
                                 <InputGroup size="sm">
                                    <InputGroupAddon addonType="prepend">
                                       <InputGroupText>
                                          <i className="fas fa-key"></i>
                                       </InputGroupText>
                                    </InputGroupAddon>
                                    <Input type="password" name="password" id="examplePassword" bsSize="sm" placeholder="password" />
                                 </InputGroup>
                              </FormGroup>
                              <Button type="submit" className="float-right" size="sm" >Login</Button>
                           </Form>
                        </CardBody>
                     </Card>
                  </Col>
               </Row>
            </div>
         </div>

      );
   }
}
