import React, { Component } from 'react';
import { Form } from 'reactstrap';
import {
   Button,
   ButtonGroup,
   FormGroup,
   InputGroup,
   HTMLTable
} from '@blueprintjs/core';

export default class EmployeeRoleForm extends Component {
   render() {
      const { role, onClear, onSubmit, onChange, buttonCaption } = this.props;
      return (
         <Form className='mb-2' ref='createProjectForm'>
            <HTMLTable condensed>
               <tr>
                  <td>
                     <FormGroup
                        label='Role'
                        labelFor='role'
                        labelInfo='(required)'
                     ></FormGroup>
                  </td>
                  <td colSpan='3'>
                     <InputGroup
                        id='role'
                        onChange={onChange}
                        placeholder='Role...'
                        type='text'
                        value={role}
                        fill
                     />
                  </td>
               </tr>
            </HTMLTable>

            <div className='text-right'>
               <ButtonGroup>
                  <Button icon='refresh' onClick={onClear}>
                     Clear
                  </Button>
                  <Button icon='saved' onClick={onSubmit}>
                     {buttonCaption}
                  </Button>
               </ButtonGroup>
            </div>
         </Form>
      );
   }
}
