import React, { Component } from 'react';
import SidebarItem from './SidebarItem';
import './Sidebar.css';

export default class Sidebar extends Component {
   /*
   state = {
      selected: 1
   };

   changeSelection(id) {
      this.setState({ selected: id });
   }
   */
   render() {
      return (
         <div>
            <nav class='side-bar no-border'>{this.props.children}</nav>
         </div>
      );
   }
}
