import React, { Component } from "react";
import { Col, Row, Button, Form, FormGroup, Label, Input } from "reactstrap";
import TopBar from "../Nav/TopBar/TopBar";
import LeftBar from "../Nav/LeftBar/LeftBar";
import NavItem from "../Nav/NavItem/NavItem";
import AppSettings from "./AppSettings/AppSettings";
import UserSettings from "./UserSettings/UserSettings";
import StdCard from "../Card/StdCard";
import Select from "../Buttons/Select/Select";
import MainContainer from "../MainContainer/MainContainer";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import CreateUser from "../Users/CreateUser";

export default class Settings extends Component {
   state = {
      theme: "dark",
      options: [
         {
            name: "a",
            value: "a"
         }
      ]
   };

   setTheme(theme) {
      if (theme === "dark") {
         return "table-dark";
      }
      if (theme === "light") {
         return "";
      }
   }
   setInputTheme(theme) {
      if (theme === "dark") {
         return "table-input-dark";
      }
      if (theme === "light") {
         return "";
      }
   }

   render() {
      return (
         <Router>
            <MainContainer theme={this.state.theme}>
               <TopBar theme={this.state.theme} />
               <LeftBar theme={this.state.theme}>
                  <Link to="/Settings/User">
                     <NavItem icon="fas fa-user-cog" />
                  </Link>
                  <Link to={`/Settings/App`}>
                     <NavItem icon="fas fa-sliders-h" />
                  </Link>
               </LeftBar>
               <Row>
                  <CreateUser />
                  <StdCard
                     icon="fas fa-align-left"
                     headerText="Application settings"
                     theme={this.state.theme}
                  />
               </Row>
            </MainContainer>
            <Route path={`/Settings/App`} component={AppSettings} />
            <Route path={`/Settings/App`} component={UserSettings} />
         </Router>
      );
   }
}
