import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

import { Card, CardTitle, CardSubtitle, CardBody, CardText } from "reactstrap";
import { Col } from "reactstrap";

import "./IconCard.css";

const pStyle = {
   "min-height": "150px"
};

/*TODO:
 
*/
export default class IconeCard extends Component {
   state = {
      theme: this.props.theme,
      disable: this.props.disable,
      name: "N/A",
      icon: "",
      proc: 0,
      number: 0,
      bgColor: "bg-c-white",
      txtColor: "text-gray"
   };

   setTheme(theme) {
      if (theme === "dark") {
         return "bg-dark text-white";
      }
      if (theme === "light") {
         return "bg-light";
      }
   }

   getTrend = value => {
      if (value < 0) return "-";
      else if (value > 0) return "+";
      else return " ";
   };

   getIcon = iconName => {
      if (!iconName) return "fas fa-bug";
      else return iconName;
   };

   getIconCardColor = disable => {
      if (disable) return "icon-card-disable";
      return "icon-card";
   };

   render() {
      const { title, icon, theme, disable } = this.props;

      return (
         <Col xl="2" lg="2" md="2" sm="4" xs="12" className="mt-4">
            <Card
               className={`text-center ${this.setTheme(
                  theme
               )} ${this.getIconCardColor(disable)}`}
            >
               <CardBody style={pStyle} className={`text-center`}>
                  <CardText>
                     <h1 className="mt-3 ">
                        <a href={this.props.link}>
                           <span
                              className={`${this.getIcon(icon)} icon-card-icon`}
                           />
                        </a>
                     </h1>
                  </CardText>
                  <CardSubtitle>{title}</CardSubtitle>
               </CardBody>
            </Card>
         </Col>
      );
   }
}
