import React, { Component } from 'react';
import {
   HTMLTable,
   Button,
   NonIdealState,
   H5,
   Spinner,
   ContextMenu,
   Dialog,
   Icon,
   Card,
   Switch
} from '@blueprintjs/core';
import Client from '../../../../Modules/Client/Client';
import UserForm from './UserForm';
import CardHeader from '../../../../components/Card/CardHeader';
import CardBody from '../../../../components/Card/CardBody';
import TableContextMenu from './TableContextMenu';

export default class UsersTable extends Component {
   state = {
      errorMsg: '',
      showEditCard: false,
      userForEdit: {},
      selected: ''
   };
   isEligable() {
      let role = Client.User.getRole();
      if (role === 'root') return false;
      if (role === 'admin') return false;
      return true;
   }
   resetErrorMsg() {
      this.setState({
         errorMsg: ''
      });
   }
   async deleteUser(id) {
      var result;
      try {
         this.resetErrorMsg();
         result = await Client.Services.UsersService.deleteUser(id);
         if (result.status === 200) {
            Client.Notifications.success('User removed successfully');
         }
      } catch (err) {
         this.setState({ errorMsg: err.response.status });
         Client.Notifications.alert('Error, cannot delete user');
      } finally {
         this.props.onRefresh();
      }
   }
   editUser(user) {
      this.setState({ showEditCard: true, userForEdit: user });
   }
   closeEditUser() {
      this.setState({ showEditCard: false });
   }
   renderTableHeader() {
      return (
         <thead>
            <tr>
               <th className='text-center pl-3'>#</th>
               <th>User Name</th>
               <th>Email</th>
               <th className='text-center'>Role</th>
               <th className='text-center'>Active</th>
            </tr>
         </thead>
      );
   }
   renderTableBody() {
      return <tbody>{this.renderTableRows()}</tbody>;
   }
   renderTableRows() {
      const { usersData } = this.props;
      return usersData.map((user, key) => {
         return (
            <tr
               key={user._id}
               onClick={e => this.handleSelection(e, user._id)}
               onContextMenu={e => this.handeClick(e, user)}
               className={`pl-2 ${
                  this.state.selected === user._id ? 'bg-primary' : ''
               }`}
            >
               <td className='text-center pl-3'>{key}</td>
               <td style={{ paddingLeft: '20px' }}>{user.username}</td>
               <td>{user.email}</td>
               <td className='text-center'>{user.usertype}</td>
               <td className='text-center'>
                  <Switch checked={user.active} />
               </td>
            </tr>
         );
      });
   }
   handeClick(e, user) {
      e.preventDefault();
      ContextMenu.show(this.renderContextMenu(e, user), {
         left: e.clientX,
         top: e.clientY
      });
   }

   handleSelection(e, id) {
      e.preventDefault();
      this.setState({
         selected: id
      });
   }

   renderContextMenu(e, user) {
      return (
         <TableContextMenu
            user={user}
            isEligable={this.isEligable()}
            onEdit={() => this.editUser(user)}
            onRemove={() => this.deleteUser(user._id)}
         />
      );
   }
   renderContent() {
      if (this.state.errorMsg) {
         return (
            <NonIdealState icon='issue'>
               <H5>
                  <strong>Something went wrong</strong>
               </H5>
               <i>Cannot load users table</i>
               <Button
                  icon='refresh'
                  minimal
                  onClick={() => {
                     this.props.onRefresh();
                     this.setState({
                        errorMsg: ''
                     });
                  }}
               ></Button>
            </NonIdealState>
         );
      }
      return (
         <div className='overflow-x-scroll'>
            <HTMLTable fill='true' striped interactive>
               {this.renderTableHeader()}
               {this.renderTableBody()}
            </HTMLTable>
         </div>
      );
   }
   render() {
      const { userForEdit } = this.state;
      const { isLoading } = this.props;

      if (isLoading) {
         return <Spinner intent='primary'></Spinner>;
      }
      return (
         <Card className='pb-0'>
            {this.renderContent()}
            <Dialog
               isOpen={this.state.showEditCard}
               onClose={() => this.closeEditUser()}
               canEscapeKeyClose={true}
            >
               <Card>
                  <CardHeader>
                     <Icon icon='user' className='header-icon' />
                     Edit User
                     <div className='float-right'>
                        <Icon
                           icon='cross'
                           onClick={() => this.closeEditUser()}
                        />
                     </div>
                  </CardHeader>
                  <CardBody className='card-body'>
                     <UserForm
                        username={userForEdit.username}
                        email={userForEdit.email}
                        role={userForEdit.role}
                        buttonCaption='Update'
                     />
                  </CardBody>
               </Card>
            </Dialog>
         </Card>
      );
   }
}
UsersTable.defaultProps = {
   usersData: [],
   onRefresh: function() {
      return;
   }
};
