import React, { Component } from "react";
import {
   Card,
   CardHeader,
   CardFooter,
   CardBody,
   CardTitle,
   CardText
} from "reactstrap";
import { Col } from "reactstrap";
import { Collapse } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import "./StdCard.css";

export default class StdCard extends Component {
   state = {
      theme: this.props.theme,
      collapse: true
   };

   setTheme(theme) {
      if (theme === "dark") {
         return "bg-dark text-white";
      }
      if (theme === "light") {
         return "bg-light";
      }
   }

   toggle = () => {
      this.setState(state => ({ collapse: !state.collapse }));
   };

   render() {
      const { icon, headerText, bodyText, cardText } = this.props;

      return (
         <Col xl="6" lg="6" md="6" sm="12" xs="12" className="mt-4">
            <Card className={this.setTheme(this.state.theme)}>
               <CardHeader>
                  <span class={`${icon} header-icon`} />
                  <div className="header-title">{headerText}</div>
                  <span
                     class="fa fa-angle-down float-right header-icon text-secondary"
                     onClick={this.toggle}
                  />
               </CardHeader>
               <Collapse isOpen={this.state.collapse}>
                  <CardBody>
                     {bodyText}
                     {this.props.children}
                     <CardText>{cardText}</CardText>
                  </CardBody>
               </Collapse>
            </Card>
         </Col>
      );
   }
}
