import React, { Component } from 'react';
import {
   Table,
   Button,
   ButtonGroup,
   Modal,
   Card,
   CardHeader,
   CardBody,
   Spinner
} from 'reactstrap';
import UpdateEmployeeForm from '../../Forms/EmployeeForms/UpdateEmployeeForm/UpdateEmployeeForm';

import axios from 'axios';
import Auth from '../../../Modules/Client/Auth/Auth';

export default class EmployeeTable extends Component {
   constructor(props) {
      super(props);
      this.getEmployees = this.getEmployees.bind(this);
      this.renderTableHeaders = this.renderTableHeaders.bind(this);
      this.renderTableData = this.renderTableData.bind(this);
      this.handleClick = this.handleClick.bind(this);
      this.closeModal = this.closeModal.bind(this);
      this.state = {
         modal: false,
         theme: this.props.theme,
         isLoading: true,
         id: '',
         sap: '',
         name: '',
         email: '',
         role: '',
         supportType: '',
         project: '',
         mainlanguage: '',
         rm: '',
         employees: []
      };
   }

   componentDidMount() {
      this.getEmployees();
   }

   getEmployees() {
      this.setState({ isLoading: true });

      const URL = '/api/employees';

      axios
         .get(URL)
         .then(res => {
            this.setState({
               employees: res.data,
               isLoading: false
            });
         })
         .catch(err => {
            alert('Error loading Employees table');
            console.log(JSON.stringify(err));
         });
   }

   setTheme() {
      const { theme } = this.state;
      if (theme === 'dark') {
         return 'bg-dark text-white table-dark';
      }
      if (theme === 'light') {
         return '';
      }
   }

   setCardTheme() {
      const { theme } = this.state;
      if (theme === 'dark') {
         return 'bg-dark text-white';
      }
      if (theme === 'light') {
         return '';
      }
   }

   addActionButton(employee) {
      return (
         <ButtonGroup>
            <Button
               size='sm'
               color='danger'
               disabled={!Auth.isAuthorizedToRemove()}
               onClick={e => this.handleClick(e, 'remove', employee)}
            >
               <span className='fas fa-trash-alt' />
            </Button>
            <Button
               size='sm'
               onClick={e => this.handleClick(e, 'edit', employee)}
            >
               <span className='fas fa-edit' />
            </Button>
         </ButtonGroup>
      );
   }

   handleClick(e, action, employee) {
      e.preventDefault();
      if (action === 'edit') this.editEmployee(employee);
      if (action === 'remove') this.removeEmployee(employee);
   }

   editEmployee(employee) {
      this.setState(prevState => ({
         modal: !prevState.modal
      }));
      this.setState({
         sap: employee.sap,
         name: employee.name,
         email: employee.email,
         role: employee.role._id,
         supportType: employee.supportType,
         project: employee.project._id,
         mainlanguage: employee.mainlanguage._id,
         rm: employee.rm._id
      });
   }

   closeModal() {
      this.setState(prevState => ({
         modal: !prevState.modal
      }));
   }

   removeEmployee(employee) {
      const URL = '/api/employees/' + employee._id;

      axios
         .delete(URL)
         .then(res => {
            alert('employee ' + employee.name + ' removed!');
            this.getEmployees();
         })
         .catch(err => {
            alert('Something went wrong :(');
         });
   }

   renderTableHeaders() {
      return (
         <tr className='bg-black text-white'>
            <th>SAP</th>
            <th>Name</th>
            <th>Role</th>
            <th>Suport Type</th>
            <th>Project</th>
            <th>Language</th>
            <th>RM</th>
            <th>Email</th>
            <th className='text-center'>Action</th>
         </tr>
      );
   }

   renderTableData() {
      return this.state.employees.map((employee, index) => {
         return (
            <tr key={employee._id}>
               <td>{employee.sap}</td>
               <td>{employee.name}</td>
               <td>{employee.role.role}</td>
               <td>{employee.supportType}</td>
               <td>{employee.project.name}</td>
               <td>{employee.mainlanguage.language}</td>
               <td>{employee.rm.name}</td>
               <td>{employee.email}</td>
               <td className='text-center'>{this.addActionButton(employee)}</td>
            </tr>
         );
      });
   }

   render() {
      const {
         theme,
         isLoading,
         modal,
         sap,
         name,
         email,
         role,
         supportType,
         project,
         mainlanguage,
         rm
      } = this.state;

      if (isLoading) {
         return (
            <div className='text-center'>
               <Spinner color='primary' />
            </div>
         );
      }

      return (
         <div>
            <p>
               All Available Employees
               <Button
                  color='primary'
                  size='sm'
                  ref='getEmployeesData'
                  id='getEmployeesData'
                  className='float-right'
                  onClick={this.getEmployees}
               >
                  <span className='fas fa-sync-alt' />
               </Button>
            </p>
            <hr />
            <Table
               responsive
               borderless
               striped
               hover
               size='sm'
               className={this.setTheme()}
            >
               <thead>{this.renderTableHeaders()}</thead>
               <tbody>{this.renderTableData()}</tbody>
            </Table>
            <Modal isOpen={modal} toggle className='modal-full modal-my'>
               <Card className={this.setCardTheme()}>
                  <CardHeader>
                     <span class='fas fa-chart-line header-icon' />
                     {'Edit Employee: ' + name}
                     <span
                        className='far fa-window-restore float-right header-icon text-secondary'
                        onClick={this.closeModal}
                     />
                  </CardHeader>

                  <CardBody className={this.setTheme()}>
                     <UpdateEmployeeForm
                        theme={theme}
                        sap={sap}
                        name={name}
                        email={email}
                        role={role}
                        supportType={supportType}
                        project={project}
                        mainlanguage={mainlanguage}
                        rm={rm}
                     />
                  </CardBody>
               </Card>
            </Modal>
         </div>
      );
   }
}

