import React, { Component } from 'react';
import { Modal, CardBody } from 'reactstrap';
import {
   Card,
   Icon,
   Button,
   ButtonGroup,
   Alert,
   Portal,
   H5
} from '@blueprintjs/core';
import DataManagerCard from '../../../../components/Card/DataManagerCard';
import StdCard from '../../../../components/Card/StdCard';
import CardHeader from '../../../../components/Card/CardHeader';
import EmployeeForm from '../../../../components/Forms/EmployeeForms/EmployeeForm';
//import EmployeeTable from '../../../../components/Table/EmployeeTable/EmployeeTable';

export default class EmployeeCard extends Component {
   state = {
      openAlert: false,
      errorMessage: '',
      sap: '',
      name: '',
      email: '',
      role: '',
      supportType: '',
      project: '',
      mainlanguage: '',
      rm: ''
   };

   render() {
      const {
         openAlert,
         errorMessage,
         sap,
         name,
         email,
         role,
         supportType,
         project,
         mainlanguage,
         rm
      } = this.state;

      return (
         <div>
            <DataManagerCard
               icon='user'
               headerText='Employees'
               cardCaption='All Employees'
               addButtonText='Add new employee'
               id='allEmployees'
               modalHeaderText='Add new employee'
               onRefresh={() =>
                  function() {
                     return;
                  }
               }
               table={<table></table>}
               addForm={
                  <EmployeeForm
                     sap={sap}
                     name={name}
                     email={email}
                     role={role}
                     supportType={supportType}
                     project={project}
                     mainlanguage={mainlanguage}
                     rm={rm}
                     onChange={this.onChange}
                     onSubmit={this.handleSubmit}
                     onClear={() => this.clearForm()}
                  />
               }
            >
               <Alert
                  isOpen={openAlert}
                  confirmButtonText='Clear form'
                  onConfirm={() => this.clearForm()}
                  cancelButtonText='Cancel'
                  onCancel={() => this.closeAlert()}
                  intent='danger'
                  icon='issue'
                  ussePortal={
                     <Portal
                        container={document.getElementById('allEmployees')}
                     />
                  }
               >
                  <H5>{errorMessage}</H5>
                  <strong>Do you want to clear the data?</strong>
               </Alert>
            </DataManagerCard>

            <StdCard icon='user' headerText='Employee Manager' size='xl'>
               <h5>
                  Manage your Employees
                  <ButtonGroup className='float-right'>
                     <Button icon='add' minimal onClick={this.toggle}>
                        Add new Employee
                     </Button>
                  </ButtonGroup>
               </h5>
               <hr />

               <Modal
                  id='createNewEmployeeModal'
                  isOpen={modal}
                  toggle={this.toggle}
                  className='modal-full modal-my'
               >
                  <Card>
                     <CardHeader>
                        <Icon icon='user' className='header-icon' />
                        Create new Employee
                        <div className='float-right'>
                           <Icon icon='minimize' onClick={this.toggle} />
                        </div>
                     </CardHeader>
                     <CardBody className='card-body'>
                        <EmployeeForm />
                     </CardBody>
                  </Card>
               </Modal>
            </StdCard>
         </div>
      );
   }
}
