import React, { Component } from 'react';
import Sidebar from '../Sidebar/Sidebar';
import SidebarItem from '../Sidebar/SidebarItem';

export default class AppSideBar extends Component {
   state = {
      selected: 'dashboard'
   };

   changeSelection(id) {
      this.setState({ selected: id });
   }

   render() {
      const { selected } = this.state;
      return (
         <Sidebar>
            <SidebarItem
               to='/Dashboard'
               icon='dashboard'
               selected={selected === 'dashboard'}
               onClick={() => this.changeSelection('dashboard')}
            />
            <SidebarItem to='/Projects' icon='projects' />
            <SidebarItem to='/Reports' icon='manually-entered-data' />
            <SidebarItem
               to='/Settings'
               icon='cog'
               text='Admin'
               selected={selected === 'admin'}
               onClick={() => this.changeSelection('admin')}
            />
            <SidebarItem to='/AdminPanel' icon='control' text='Admin' />
         </Sidebar>
      );
   }
}
