import React, { Component } from 'react';
import './TopBar.css';
import { Redirect, Link } from 'react-router-dom';
import {
   UncontrolledDropdown,
   DropdownToggle,
   DropdownMenu,
   DropdownItem
} from 'reactstrap';
import Auth from '../../../Modules/Auth/Auth';

export default class TopBar extends Component {
   constructor(props) {
      super(props);
      this.setState = this.setState.bind(this);
      this.handleLogout = this.handleLogout.bind(this);
      this.state = {
         logout: false
      };
   }

   setTheme(theme) {
      if (theme === 'dark') {
         return 'navbar-dark bg-dark text-white';
      }
      if (theme === 'light') {
         return 'navbar-light bg-light';
      } else {
         return 'navbar-light bg-light';
      }
   }

   handleLogout = e => {
      e.preventDefault();
      Auth.deauthenticateUser();
      this.setState({
         logout: true
      });
   };

   render() {
      if (this.state.logout) {
         return <Redirect to='/login' />;
      }

      return (
         <nav
            className={`navbar fixed-top navbar-expand ${this.setTheme(
               this.props.theme
            )}`}
         >
            <Link to='/Home' className='navbar-brand'>
               <span className='fas fa-home' />
            </Link>
            <ul className='navbar-nav mr-auto'>
               <li className='nav-icon'>
                  <UncontrolledDropdown nav>
                     <DropdownToggle nav className='nav-icon'>
                        <span className='fas fa-th' />
                     </DropdownToggle>
                     <DropdownMenu
                        left
                        className={this.setTheme(this.props.theme)}
                     >
                        <DropdownItem
                           className={this.setTheme(this.props.theme)}
                        >
                           Get Help
                        </DropdownItem>
                     </DropdownMenu>
                  </UncontrolledDropdown>
               </li>
               {this.props.children}
            </ul>
            <ul className='navbar-nav'>
               <li className='nav-icon'>
                  <UncontrolledDropdown nav size='lg'>
                     <DropdownToggle nav className='nav-icon'>
                        <span className='fas fa-bars' />
                     </DropdownToggle>
                     <DropdownMenu
                        left
                        className={this.setTheme(this.props.theme)}
                     >
                        <DropdownItem
                           onClick={this.handleLogout}
                           className={this.setTheme(this.props.theme)}
                        >
                           <span className='fas fa-power-off mr-2 ml-2' />
                           Logut
                        </DropdownItem>
                     </DropdownMenu>
                  </UncontrolledDropdown>
               </li>
            </ul>
         </nav>
      );
   }
}
