import React, { Component } from "react";
import { Table } from "reactstrap";

import "bootstrap/dist/css/bootstrap.min.css";
import "@fortawesome/fontawesome-free";

export default class TeamTable extends Component {
   state = {
      theme: this.props.theme,
      data: this.props.data,
      _testDataSet: [
         {
            id: 1,
            project: "L&D",
            employeeName: "Rafal Cymbalista",
            sap: "123123",
            role: "Manager",
            mainLanguage: "English",
            mainLanguageLevel: "C1",
            newJoiner: "False",
            doj: "12/11/2015",
            rm: "Mateusz Golba"
         },
         {
            id: 1,
            project: "L&D",
            employeeName: "Rafal Cymbalista",
            sap: "123123",
            role: "Manager",
            mainLanguage: "English",
            mainLanguageLevel: "C1",
            newJoiner: "False",
            doj: "12/11/2015",
            rm: "Mateusz Golba"
         }
      ]
   };

   /*TODO:
      -- Function to strip the data into the table values
         -- data structure : Headers + values
   */

   setTheme() {
      const { theme } = this.props;
      if (theme === "dark") {
         return "table-dark";
      }
      if (theme === "light") {
         return "";
      }
   }

   renderTableHeaders() {
      let header = Object.keys(this.state._testDataSet[0]);
      return header.map((key, index) => {
         return <th key={index}>{key}</th>;
      });
   }

   renderTableData() {
      return this.state._testDataSet.map((user, index) => {
         const {
            id,
            project,
            employeeName,
            sap,
            role,
            mainLanguage,
            mainLanguageLevel,
            newJoiner,
            doj,
            rm
         } = user;
         return (
            <tr key={id}>
               <td>{id}</td>
               <td>{project}</td>
               <td>{employeeName}</td>
               <td>{sap}</td>
               <td>{role}</td>
               <td>{mainLanguage}</td>
               <td>{mainLanguageLevel}</td>
               <td>{newJoiner}</td>
               <td>{doj}</td>
               <td>{rm}</td>
            </tr>
         );
      });
   }

   render() {
      return (
         <div>
            <Table
               responsive
               borderless
               striped
               hover
               size="sm"
               className={this.setTheme()}
            >
               <thead>
                  <tr>{this.renderTableHeaders()}</tr>
               </thead>
               <tbody>{this.renderTableData()}</tbody>
            </Table>
         </div>
      );
   }
}
