import React, { Component } from "react";
import TopBar from "../../components/Nav/TopBar/TopBar";
import MainContainer from "../../components/MainContainer/MainContainer";
import "@fortawesome/fontawesome-free";
import { Row } from "reactstrap";
import IconCard from "../../components/Card/IconCard/IconCard";
import StdCard from "../../components/Card/StdCard";
import NavItem from "../../components/Nav/NavItem/NavItem";
import { BrowserRouter as Router } from "react-router-dom";

export default class Home extends Component {
   constructor(props) {
      super(props);
      this.state = {
         theme: "light"
      };
      this.switchTheme = this.switchTheme.bind(this);
   }

   switchTheme() {
      const { theme } = this.state;

      if (theme === "dark") {
         this.setState({ theme: "light" });
      }
      if (theme === "light") {
         this.setState({ theme: "dark" });
      }
      if (!theme) {
         this.setState({ theme: "light" });
      }
   }

   render() {
      return (
         <MainContainer theme={this.state.theme}>
            <TopBar theme={this.state.theme}>
               <NavItem
                  icon="fas fa-adjust text-right"
                  onClick={this.switchTheme}
               />
            </TopBar>
            <div className="container-fluid">
               <Router>
                  <Row>
                     <IconCard
                        icon="fas fa-chart-line"
                        theme={this.state.theme}
                        title="Dashboard"
                        link="/MainDashboard/"
                     />
                     <IconCard
                        icon="fas fa-users"
                        theme={this.state.theme}
                        title="Teams"
                        disable={true}
                     />
                     <IconCard
                        icon="fas fa-cogs"
                        theme={this.state.theme}
                        title="Settings"
                        link="/Settings/"
                     />
                     <IconCard
                        icon="fas fa-project-diagram"
                        theme={this.state.theme}
                        title="Project setup"
                     />
                     <IconCard
                        icon="fas fa-file-invoice"
                        theme={this.state.theme}
                        title="Report"
                     />

                     <IconCard
                        icon="fas fa-user-cog"
                        theme={this.state.theme}
                        title="User"
                     />
                  </Row>
                  <Row>
                     <StdCard
                        theme={this.state.theme}
                        icon="fas fa-rss"
                        headerText="Project info"
                        size="xl"
                     >
                        <h3> Project info </h3>
                        <hr />
                        <h5>current version: 0.0.1</h5>
                        <hr />
                        <h5>Author: Rafal Cymbalista</h5>
                     </StdCard>
                  </Row>
               </Router>
            </div>
         </MainContainer>
      );
   }
}
