import React, { Component } from 'react';
import { Row, Col } from 'reactstrap';
import { Toaster, Toast, Intent } from '@blueprintjs/core';
import CreateuserForm from './components/CreateUserForm';
//import Client from '../../Modules/Client/Client';

export default class AdminPanel extends Component {
   state = {
      toasts: [],
      username: '',
      password: '',
      role: 'USER'
   };

   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };
   handleSubmit = async e => {
      e.preventDefault();
      //TODO:
      // logic to create user to be implemented inside Client
      this.addToast();
   };
   addToast = () => {
      const doToast = Toaster.create({
         position: 'top-right',
         maxToasts: 5
      });
      doToast.show({ message: 'something' });

      //<Toast icon='info-sign' message='asd' intent='success'></Toast>
   };
   render() {
      const { username, password } = this.state;
      return (
         <div>
            <Row>
               <Col sx='12' sm='6'>
                  <CreateuserForm
                     username={username}
                     password={password}
                     onChange={this.onChange}
                     onClick={this.handleSubmit}
                  />
               </Col>
            </Row>
         </div>
      );
   }
}
