import React, { Component } from 'react';
import {
   Navbar,
   Button,
   Alignment,
   Menu,
   MenuItem,
   MenuDivider,
   Popover,
   Position
} from '@blueprintjs/core';
import UIManager from '../../Modules/UIManager/UIManager';

export default class AppBar extends Component {
   constructor(props) {
      super(props);
      this.state = {
         themeIcon: UIManager.isDarkThemeEnabled() ? 'flash' : 'moon',
         theme: UIManager.isDarkThemeEnabled() ? 'dark' : 'light'
      };
      this.switchTheme = this.switchTheme.bind(this);
   }

   switchTheme() {
      UIManager.swtichTheme();
      this.setState({
         themeIcon: UIManager.isDarkThemeEnabled() ? 'flash' : 'moon',
         theme: UIManager.isDarkThemeEnabled() ? 'light' : 'dark'
      });
   }

   render() {
      var exampleMenu = (
         <Menu>
            <MenuItem icon='user' text='Profile' />
            <MenuItem icon='cog' text='Settings...'></MenuItem>
            <MenuItem
               icon={this.state.themeIcon}
               text={'Activate ' + this.state.theme + ' mode'}
               onClick={this.switchTheme}
            />
            <MenuDivider />
            <MenuItem icon='log-out' text='Logout...'></MenuItem>
         </Menu>
      );
      return (
         <Navbar fixedToTop>
            <Navbar.Group align={Alignment.LEFT}>
               <Button minimal icon='home' text='' small />
               <Navbar.Divider />
            </Navbar.Group>
            <Navbar.Group align={Alignment.RIGHT}>
               <Popover
                  content={exampleMenu}
                  position={Position.BOTTOM}
                  minimal={true}
               >
                  <Button className='bp3-minimal' icon='menu' text='' />
               </Popover>
            </Navbar.Group>
         </Navbar>
      );
   }
}
