import React, { Component } from 'react';
import Chart from 'react-apexcharts';
import { Card } from '@blueprintjs/core';
import UIManager from '../../../Modules/UIManager/UIManager';

export default class LineChart extends Component {
   constructor(props) {
      super(props);

      this.state = {
         options: {
            chart: {
               id: 'basic-bar'
            },
            theme: { mode: UIManager.getTheme() },
            xaxis: {
               type: 'category',
               categories: [
                  1991,
                  1992,
                  1993,
                  1994,
                  1995,
                  1996,
                  1997,
                  1998,
                  1999
               ],

               axisBorder: {
                  show: true,
                  color: '#78909C',
                  height: 1,
                  width: '100%',
                  offsetX: 0,
                  offsetY: 0
               },

               tickAmount: undefined,
               tickPlacement: 'between',
               min: undefined,
               max: undefined,
               range: undefined,
               floating: false,
               position: 'bottom',
               title: {
                  text: undefined,
                  offsetX: 0,
                  offsetY: 0,
                  style: {
                     color: undefined,
                     fontSize: '12px',
                     fontFamily: 'Helvetica, Arial, sans-serif',
                     cssClass: 'chart-text'
                  }
               },
               crosshairs: {
                  show: true,
                  width: 1,
                  position: 'back',
                  opacity: 0.9,
                  stroke: {
                     color: '#b6b6b6',
                     width: 0,
                     dashArray: 0
                  },
                  fill: {
                     type: 'solid',
                     color: '#B1B9C4',
                     gradient: {
                        colorFrom: '#D8E3F0',
                        colorTo: '#BED1E6',
                        stops: [0, 100],
                        opacityFrom: 0.4,
                        opacityTo: 0.5
                     }
                  },
                  dropShadow: {
                     enabled: false,
                     top: 0,
                     left: 0,
                     blur: 1,
                     opacity: 0.4
                  }
               },
               tooltip: {
                  enabled: true,
                  formatter: undefined,
                  offsetY: 0,
                  style: {
                     fontSize: 0,
                     fontFamily: 0
                  }
               }
            }
         },
         series: [
            {
               name: 'series-1',
               data: [30, 40, 45, 50, 49, 60, 70, 91]
            }
         ]
      };
   }
   render() {
      return (
         <Card>
            <Chart
               options={this.state.options}
               series={this.state.series}
               type='bar'
               width='100%'
            />
         </Card>
      );
   }
}
