import React, { Component } from 'react';
import '../../App.css';
import './MainContainer.css';

export default class MainContainer extends Component {
   state = {
      displayBgImage: this.displayBgImage(this.props.displayBgImage),
      app: (this.props.app = true),
      home: (this.props.home = false)
   };

   displayBgImage(disply) {
      if (disply) {
         return 'bg-image';
      }
      return '';
   }

   selectPlace() {
      const { app, home } = this.props;
      if (app) {
         return 'main-data-app';
      }
      if (home) {
         return 'main-data-home';
      }
      return '';
   }

   render() {
      return (
         <div
            className={`${this.displayBgImage(this.props.displayBgImage)}
                        ${this.selectPlace()}`}
            id='MainContainer'
         >
            {this.props.children}
         </div>
      );
   }
}
