import React, { Component } from 'react';
import {
   Col,
   Alert,
   Button,
   Form,
   FormGroup,
   FormText,
   Label,
   Input,
   ButtonGroup
} from 'reactstrap';
import axios from 'axios';

import LanguageOptions from '../../Options/LanguageOptions';
import RoleOptions from '../../Options/RoleOptions';
import ProjectOptions from '../../Options/ProjectOptions';
import RMOptions from '../../Options/RMOptions';
import SupportTypeOptions from '../../Options/SupportTypeOptions';

/**
 *    TODO:
 *       -- Create init values
 */

export default class UpdateEmployeeForm extends Component {
   state = {
      theme: this.props.theme,
      sap: this.props.sap,
      name: this.props.name,
      email: this.props.email,
      role: this.props.role,
      supportType: this.props.supportType,
      project: this.props.project,
      mainlanguage: this.props.mainlanguage,
      rm: this.props.rm,
      message: ''
   };

   onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
   };

   onSubmit = e => {
      e.preventDefault();

      const {
         sap,
         name,
         email,
         role,
         supportType,
         project,
         mainlanguage,
         rm
      } = this.state;

      const URL = '/api/employees/';

      const data = {
         employee: {
            sap: sap,
            name: name,
            email: email,
            role: role,
            supportType: supportType,
            project: project,
            mainlanguage: mainlanguage,
            rm: rm
         }
      };

      axios
         .put(URL, data)
         .then(res => {
            this.setState({
               created: 'ok',
               message: 'Employee updated successfully'
            });
            document.getElementById('getEmployeesData').click();
         })
         .catch(err => {
            this.setState({
               created: 'error',
               message: 'Something went wrong',
               err: err
            });
         });
   };
   resetForm = () => {
      this.setState({
         sap: this.props.sap,
         name: this.props.name,
         email: this.props.email,
         role: this.props.role,
         supportType: this.props.supportType,
         project: this.props.project,
         mainlanguage: this.props.mainlanguage,
         rm: this.props.rm,
         message: ''
      });
   };
   setInputTheme() {
      const { theme } = this.props;
      if (theme === 'dark') {
         return 'bg-secondary text-white no-border';
      }
      if (theme === 'light') {
         return '';
      }
   }
   alertStyle() {
      const { created } = this.state;
      if (created === 'ok') return 'border-success text-success';
      if (created === 'error') return 'border-danger text-danger';
   }

   render() {
      const {
         sap,
         name,
         email,
         role,
         supportType,
         project,
         mainlanguage,
         rm,
         message
      } = this.state;

      return (
         <div>
            <h5>Update Employee</h5>
            {message !== '' && (
               <Alert
                  className={`bg-transparent border rounded-corners text-center ${this.alertStyle()}`}
               >
                  {message}
               </Alert>
            )}
            <Form className='mb-2' ref='createProjectForm'>
               <FormGroup row>
                  <Label className='text-right' for='sap' md={3} size='sm'>
                     SAP
                  </Label>
                  <Col md={3}>
                     <Input
                        type='number'
                        name='sap'
                        id='sap'
                        ref='sap'
                        size='sm'
                        value={sap}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide SAP Number</FormText>
                  </Col>
                  <Label className='text-right' for='email' md={2} size='sm'>
                     Email
                  </Label>
                  <Col md={4}>
                     <Input
                        type='email'
                        name='email'
                        id='email'
                        ref='email'
                        size='sm'
                        value={email}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide email</FormText>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='name' md={3} size='sm'>
                     Name
                  </Label>
                  <Col md={3}>
                     <Input
                        type='text'
                        name='name'
                        id='name'
                        ref='name'
                        size='sm'
                        value={name}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide Employee Name</FormText>
                  </Col>
                  <Label className='text-right' for='role' md={2} size='sm'>
                     Role
                  </Label>
                  <Col md={4}>
                     <RoleOptions
                        name='role'
                        value={role}
                        onChange={this.onChange}
                        className={this.setInputTheme()}
                     />
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='email' md={3} size='sm'>
                     Support Type
                  </Label>
                  <Col md={3}>
                     <SupportTypeOptions
                        name='supportType'
                        value={supportType}
                        onChange={this.onChange}
                        className={this.setInputTheme()}
                     />
                  </Col>
                  <Label className='text-right' for='Project' md={2} size='sm'>
                     Project
                  </Label>
                  <Col md={4}>
                     <ProjectOptions
                        name='project'
                        value={project}
                        onChange={this.onChange}
                        className={this.setInputTheme()}
                     />
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label
                     className='text-right'
                     for='mainlanguage'
                     md={3}
                     size='sm'
                  >
                     Main Language
                  </Label>
                  <Col md={3}>
                     <LanguageOptions
                        name='mainlanguage'
                        value={mainlanguage}
                        onChange={this.onChange}
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide main language</FormText>
                  </Col>
                  <Label className='text-right' for='rm' md={2} size='sm'>
                     Reporting Manager
                  </Label>
                  <Col md={4}>
                     <RMOptions
                        name='rm'
                        value={rm}
                        onChange={this.onChange}
                        className={this.setInputTheme()}
                     />
                  </Col>
               </FormGroup>
               <div className='text-right'>
                  <ButtonGroup>
                     <Button size='sm' onClick={this.resetForm}>
                        Reset
                     </Button>
                     <Button color='primary' size='sm' onClick={this.onSubmit}>
                        Update
                     </Button>
                  </ButtonGroup>
               </div>
            </Form>
         </div>
      );
   }
}
