import React, { Component } from 'react';
import { TabContent, TabPane } from 'reactstrap';

import ProjectCard from '../Card/ProjectCard/ProjectsManagerCard';
import SLACard from '../Card/SLACard/SLACard';
import EmployeeCard from '../Card/EmployeeCard/EmployeeCard';

export default class MainProjects extends Component {
   constructor(props) {
      super(props);
      this.state = {
         showReportFilter: false,
         activeTab: '1'
      };
   }

   switchTab(tab) {
      if (this.state.activeTab !== tab) {
         this.setState({
            activeTab: tab
         });
      }
   }

   switchShowReportFilter = () => {
      this.setState(state => ({ showReportFilter: !state.showReportFilter }));
   };

   render() {
      const { theme } = this.state;

      return (
         <TabContent activeTab={this.state.activeTab} className='mb-5'>
            <TabPane tabId='1'>
               <ProjectCard theme={theme} />
            </TabPane>
            <TabPane tabId='2'>
               <SLACard theme={theme} />
            </TabPane>
            <TabPane tabId='3'>
               <EmployeeCard theme={theme} />
            </TabPane>
            <TabPane tabId='4'>Review notes</TabPane>
         </TabContent>
      );
   }
}
