import API from '../../API';
import Request from '../../Requests/Request';

export default class EmployeesService {
   static EMPLOYEES_ROUTES = API.ROUTES.EMPLOYEES;
   static EMPLOYEES_ROLES_ROUTES = API.ROUTES.EMPLOYEES_ROLES;

   static async getEmployees() {
      return await Request.get(this.EMPLOYEES_ROUTES.GET_EMPLOYEES)
         .then(result => {
            return result.data;
         })
         .catch(err => {
            return err;
         });
   }

   static async getEmployeeByName(name) {
      return await Request.get(
         this.EMPLOYEES_ROUTES.GET_EMPLOYEE_BY_NAME + name
      )
         .then(result => {
            return result.data;
         })
         .catch(err => {
            return err;
         });
   }

   static async getEmployeeById(id) {
      return await Request.get(this.EMPLOYEES_ROUTES.GET_EMPLOYEE_BY_ID + id)
         .then(result => {
            return result.data;
         })
         .catch(err => {
            return err;
         });
   }

   static async createEmployee(employee) {
      return await Request.post(this.EMPLOYEES_ROUTES.CREATE_EMPLOYEE, employee)
         .then(result => {
            return result.data;
         })
         .catch(err => {
            return err;
         });
   }

   static async updateEmployee(employee) {
      return await Request.put(this.EMPLOYEES_ROUTES.UPDATE_EMPLOYEE, employee)
         .then(result => {
            return result.data;
         })
         .catch(err => {
            return err;
         });
   }

   static async deleteEmployeeById(id) {
      return await Request.delete(
         this.EMPLOYEES_ROUTES.DELETE_EMPLOYEE_BY_ID + id
      )
         .then(result => {
            return result.data;
         })
         .catch(err => {
            return err;
         });
   }

   static async getEmployeesRoles() {
      return await Request.get(this.EMPLOYEES_ROLES_ROUTES.GET_EMPLOYEES_ROLES)
         .then(result => {
            return result.data;
         })
         .catch(err => {
            return err;
         });
   }

   static async getEmployeesRoleById(id) {
      return await Request.get(
         this.EMPLOYEES_ROLES_ROUTES.GET_EMPLOYEES_ROLE_BY_ID + id
      )
         .then(result => {
            return result.data;
         })
         .catch(err => {
            return err;
         });
   }

   static async createEmployeeRole(role) {
      return await Request.post(
         this.EMPLOYEES_ROLES_ROUTES.CREATE_EMPLOYEES_ROLE,
         role
      )
         .then(result => {
            return result.data;
         })
         .catch(err => {
            return err;
         });
   }

   static async deleteEmployeeRoleById(id) {
      return await Request.delete(
         this.EMPLOYEES_ROLES_ROUTES.DELETE_EMPLOYEES_ROLE_BY_ID + id
      )
         .then(result => {
            return result.data;
         })
         .catch(err => {
            return err;
         });
   }
}
