import React, { Component } from 'react';
import { Button } from '@blueprintjs/core';
import { Link } from 'react-router-dom';

import './Sidebar.css';
import SidebarItem from './SidebarItem';

export default class Sidebar extends Component {
   state = {
      selected: 1
   };

   changeSelection(id) {
      this.setState({ selected: id });
   }

   render() {
      return (
         <div>
            <nav class='side-bar no-border'>
               <Link to='/Dashboards'>
                  <Button
                     minimal
                     large
                     icon='dashboard'
                     fill
                     active={this.state.selected === 1}
                     intent={this.state.selected === 1 ? 'primary' : null}
                     onClick={() => this.changeSelection(1)}
                  />
               </Link>
               <Link to='/Projects'>
                  <Button
                     minimal
                     large
                     icon='projects'
                     fill
                     active={this.state.selected === 2}
                     intent={this.state.selected === 2 ? 'primary' : null}
                     onClick={() => this.changeSelection(2)}
                  />
               </Link>
               <Link to='/Reports'>
                  <Button
                     minimal
                     large
                     icon='join-table'
                     fill
                     active={this.state.selected === 3}
                     intent={this.state.selected === 3 ? 'primary' : null}
                     onClick={() => this.changeSelection(3)}
                  />
               </Link>
               <Link to='/Targets'>
                  <Button
                     minimal
                     large
                     icon='locate'
                     fill
                     active={this.state.selected === 4}
                     intent={this.state.selected === 4 ? 'primary' : null}
                     onClick={() => this.changeSelection(4)}
                  />
               </Link>
               <Link to='/Settings'>
                  <Button
                     minimal
                     large
                     icon='cog'
                     fill
                     active={this.state.selected === 5}
                     intent={this.state.selected === 5 ? 'primary' : null}
                     onClick={() => this.changeSelection(5)}
                  />
               </Link>
               <Link to='/AdminPanel'>
                  <Button
                     minimal
                     large
                     icon='wrench'
                     fill
                     active={this.state.selected === 6}
                     intent={this.state.selected === 6 ? 'primary' : null}
                     onClick={() => this.changeSelection(6)}
                  />
               </Link>
               <SidebarItem
                  link='/UserProfile'
                  icon='user'
                  selected={this.state.selected === 7}
                  onClick={() => this.changeSelection(7)}
               ></SidebarItem>
            </nav>
         </div>
      );
   }
}
const homeButton = () => {
   return (
      <Button
         minimal
         large
         icon='home'
         fill
         active={this.state.selected === 0}
         intent={this.state.selected === 0 ? 'primary' : null}
         onClick={() => this.changeSelection(0)}
      ></Button>
   );
};

