import React, { Component } from 'react';
import './App.css';

import 'bootstrap/dist/css/bootstrap.min.css'

import Navbar from './components/NavBar/Navbar'
import Card from './components/Card/Card'
import LeftNav from './components/LeftNav/LeftNav';
import CardWithChart from './components/Card/ChartWithChart'
import CardScore from './components/Card/CardScore';

class App extends Component {

  state = { users: [] }

  componentDidMount() {
    fetch('/users')
      .then(res => res.json())
      .then(users => this.setState({ users }))
  }

  render() {
    return (
      <div className="App">
        <Navbar></Navbar>
        <LeftNav></LeftNav>

          <div className="container">
            <div className="row">
              <Card className="col-6" headerText="Users" bodyText=
                {this.state.users.map(user =>
                  <div key={user.id}>{user.username}</div>
                )}
              >
              </Card>
              <CardScore className="col-6" title="Score" mainval="5%" secondval="3%"></CardScore>
            </div>
            <div className="row">
              <CardWithChart className="col-6" headerText="Chart_testing"></CardWithChart>
            </div>
            <div className="row">
              <CardScore className="col-2" headerText="Chart_testing"></CardScore>
              <CardScore className="col-2" headerText="Chart_testing"></CardScore>
              <CardScore className="col-2" headerText="Chart_testing"></CardScore>
              <CardScore className="col-2" headerText="Chart_testing"></CardScore>
              <CardScore className="col-2" headerText="Chart_testing"></CardScore>
              <CardScore className="col-2" headerText="Chart_testing"></CardScore>
              <CardScore className="col-2" headerText="Chart_testing"></CardScore>
              <CardScore className="col-2" headerText="Chart_testing"></CardScore>
            </div>
          </div>

      </div>
    );
  }
}

export default App;
