import React, { Component } from 'react';
import { Alert, H5 } from '@blueprintjs/core';

import DataManagerCard from '../../../../components/Card/DataManagerCard';
import CreateSLAFrom from '../../../../components/Forms/SLAsForms/CreateSLAForm';
//import EditSLAform from '../../../../components/Forms/SLAsForms/EditSLAform';

export default class SLACard extends Component {
   state = {
      theme: this.props.theme,
      errorMessage: '',
      openAlert: false,
      sms: [],
      ops: []
   };

   render() {
      const { openAlert, errorMessage } = this.state;
      return (
         <DataManagerCard
            icon='locate'
            headerText='SLAs'
            cardCaption='All SLAs in my Projects'
            addButtonText='Add new SLA'
            id='allSLAs'
            modalHeaderText='Add new SLA'
            table={'table to be added...'}
            addForm={
               <CreateSLAFrom
                  onChange={this.onChange}
                  onClick={this.handleSubmit}
                  buttonCaption='Create'
               />
            }
         >
            <Alert
               isOpen={openAlert}
               confirmButtonText='Clear form'
               onConfirm={() => this.clearForm()}
               cancelButtonText='Cancel'
               onCancel={() => this.closeAlert()}
               intent='danger'
               icon='issue'
               portalContainer={document.getElementById('allUsers')}
            >
               <H5>Error</H5>
               <H5>{errorMessage}</H5>
               <p>Do you want to clear the data?</p>
            </Alert>
         </DataManagerCard>
      );
   }
}
