import React, { Component } from "react";
import {
   Card,
   CardBody,
   CardTitle,
   Collapse,
   Button,
   Input,
   ButtonGroup,
   TabContent,
   TabPane,
   Nav,
   NavItem,
   NavLink,
   Row,
   Col
} from "reactstrap";

import classnames from "classnames";
import "./ReportControl.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "@fortawesome/fontawesome-free/css/all.min.css";

export default class ReportControl extends Component {
   state = {
      theme: this.props.theme,
      value: new Date().toISOString(),
      collapse: false,
      activeTab: "1"
   };

   setTheme(theme) {
      if (theme === "dark") {
         return "bg-dark text-white";
      }
      if (theme === "light") {
         return "bg-light";
      }
   }

   switchTab(tab) {
      if (this.state.activeTab !== tab) {
         this.setState({
            activeTab: tab
         });
      }
   }

   render() {
      return (
         <div className="report-control mt-0">
            <Row>
               <Col>
                  <Card className={`mt-0 ${this.setTheme(this.state.theme)}`}>
                     <Collapse isOpen={this.props.show}>
                        <CardBody className="no-border">
                           <CardTitle>
                              <span class="fas fa-filter header-icon text-secondary" />
                              Apply Filter
                           </CardTitle>
                           <Nav pills>
                              <NavItem>
                                 <NavLink
                                    className={classnames({
                                       active: this.state.activeTab === "1"
                                    })}
                                    onClick={() => {
                                       this.switchTab("1");
                                    }}
                                 >
                                    <span class="fas fa-history float-left button-icon" />
                                    By Timeline
                                 </NavLink>
                              </NavItem>
                              <NavItem>
                                 <NavLink
                                    className={classnames({
                                       active: this.state.activeTab === "2"
                                    })}
                                    onClick={() => {
                                       this.switchTab("2");
                                    }}
                                 >
                                    <span class="fas fa-user-cog float-left button-icon" />
                                    By Reporting Structure
                                 </NavLink>
                              </NavItem>
                           </Nav>
                           <TabContent activeTab={this.state.activeTab}>
                              <TabPane tabId="1">
                                 <Row>
                                    <Col sm="12">
                                       <p>Time filters</p>
                                       <Input type="date" />
                                    </Col>
                                 </Row>
                              </TabPane>
                              <TabPane tabId="2">
                                 <Row>
                                    <Col sm="12">
                                       <p>Service Manger filter</p>
                                       <ButtonGroup size="sm">
                                          <Button>Tomek</Button>
                                          <Button>Ewa</Button>
                                          <Button>Ktostam</Button>
                                       </ButtonGroup>
                                       <hr />
                                       <p>Projects</p>
                                       <ButtonGroup size="sm">
                                          <Button>Projekt_1</Button>
                                          <Button>Projekt_@</Button>
                                       </ButtonGroup>
                                    </Col>
                                 </Row>
                              </TabPane>
                           </TabContent>
                           <hr />
                           <Button color="primary">Apply</Button>
                        </CardBody>
                     </Collapse>
                  </Card>
               </Col>
            </Row>
         </div>
      );
   }
}
