import React, { Component } from 'react';
import { Tabs, Tab, H2, Alert, Portal, H5, Classes } from '@blueprintjs/core';
import EmployeesTable from '../../components/Tables/EmployeesTable';
import EmployeeDialog from '../../components/Dialogs/EmployeeDialog';
import Client from '../../Modules/Client/Client';
import TableControllers from '../../components/Tables/TableControllers';

export default class Employees extends Component {
   state = {
      activeTabID: 'Employees',
      selectedTabId: 'Employees',
      isLoading: true,
      data: [],
      openAlert: true,
      errorMessage: '',
      isEmployeesDialogOpen: false,
      username: '',
      email: '',
      password: '',
      usertype: '',
      selected: {},
      isUserEditDialogOpen: false,
      isDeleteAlertOpen: false,
      canEdit: !false,
      canDelete: !false
   };

   getSelected = (emp, isSelected) => {
      this.setState({
         selected: emp,
         canEdit: isSelected,
         canDelete: isSelected
      });
   };

   componentDidMount() {
      this.getEmployeesData();
   }

   async getEmployeesData() {
      this.setLoading(true);
      var result;
      try {
         //this.resetErrorMsg();
         result = await Client.Services.EmployeesService.getEmployees();
         this.setState({
            data: result.data
         });
         console.log(result.data);
      } catch (err) {
         alert(err);
         this.setState({ errorMsg: err.response });
         Client.Notifications.alert('Error while loading Employees list');
      } finally {
         this.setLoading(false);
      }
   }

   async deleteUser(id) {
      var result;
      try {
         this.resetErrorMsg();
         result = await Client.Services.EmployeesService.deleteUser(id);
         if (result.status === 200) {
            Client.Notifications.success('User removed successfully');
         }
      } catch (err) {
         this.setState({ errorMsg: err.response.status });
         Client.Notifications.alert('Error, cannot delete user');
      } finally {
         this.props.onRefresh();
      }
   }

   handleSubmit = async e => {
      e.preventDefault();
      const { email, username, usertype, password } = this.state;
      var errorMsg = 'Unknown Error';
      let usr = Client.Services.EmployeesService.createUserDTO(
         username,
         email,
         password,
         usertype
      );
      try {
         let result = await Client.Services.EmployeesService.saveNewUser(usr);
         if (result.status === 200) {
            Client.Notifications.success(`User ${username} successfully saved`);
            this.getEmployeesData();
         }
      } catch (err) {
         if (err.response.status === 409) {
            errorMsg = `Error: ${err.response.data.message}`;
            Client.Notifications.alert(errorMsg);
         }
         if (err.response.status !== 409) {
            errorMsg = `Error:  while saving: ${username} user`;
            Client.Notifications.error(errorMsg);
         }
         this.openAlert(errorMsg);
      }
   };
   openAlert(msg) {
      this.setState({ isAlertOpen: true, errorMessage: msg });
   }
   closeAlert() {
      this.setState({ isAlertOpen: false, errorMessage: '' });
   }

   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };

   openEmployeesDialog() {
      this.setState({
         isEmployeesDialogOpen: true
      });
   }
   closeEmployeesDialog() {
      this.setState({
         isEmployeesDialogOpen: false
      });
   }

   openUserEditDialog() {
      this.setState({
         isUserEditDialogOpen: true
      });
   }
   closeUserEditDialog() {
      this.setState({
         isUserEditDialogOpen: false
      });
   }

   openDeleteAlert() {
      this.setState({
         isDeleteAlertOpen: true
      });
   }
   closeDeleteAlert() {
      this.setState({
         isDeleteAlertOpen: false
      });
   }

   setLoading(loading) {
      this.setState({
         isLoading: loading
      });
   }

   handleTabChange = activeTabID => {
      this.setState({
         activeTabID: activeTabID,
         selectedTabId: activeTabID
      });
   };
   clearForm() {
      this.setState({
         email: '',
         username: '',
         usertype: 'standard',
         password: '',
         isAlertOpen: false,
         errorMessage: ''
      });
   }

   renderControlls() {
      if (this.state.selectedTabId === 'Employees') {
         return (
            <TableControllers
               onEdit={() => this.openUserEditDialog()}
               canEdit={this.state.canEdit}
               onAdd={() => this.openEmployeesDialog()}
               onDelete={() => this.openDeleteAlert()}
               canDelete={this.state.canDelete}
               onRefresh={() => this.getEmployeesData()}
            />
         );
      }
   }

   render() {
      const {
         data,
         isLoading,
         email,
         name,
         team,
         project,
         user,
         type,
         isAlertOpen,
         selected
      } = this.state;

      return (
         <div id='AdminPanel'>
            <div className='mt-2'>
               <H2>Employees Panel</H2>
            </div>
            <Tabs
               onChange={this.handleTabChange}
               selectedTabId={this.state.selectedTabId}
               large
            >
               <Tab
                  id='Employees'
                  title='Employees'
                  panel={
                     <EmployeesTable
                        data={data}
                        onRefresh={() => this.getEmployeesData()}
                        isLoading={isLoading}
                        onSelection={this.getSelected}
                     />
                  }
               />
               <Tabs.Expander />
               {this.renderControlls()}
            </Tabs>
            <EmployeeDialog
               isOpen={this.state.isUserEditDialogOpen}
               toOpen={() => this.openUserEditDialog()}
               toClose={() => this.closeUserEditDialog()}
               name={name}
               email={email}
               user={user}
               type={type}
               project={project}
               team={team}
               onChange={this.onChange}
               handleSubmit={this.handleSubmit}
               buttonCaption='Add'
               onClear={() => this.clearForm()}
               isAlertOpen={isAlertOpen}
               closeAlert={() => this.closeAlert()}
            />
            <EmployeeDialog
               isOpen={this.state.isEmployeesDialogOpen}
               toOpen={() => this.openEmployeesDialog()}
               toClose={() => this.closeEmployeesDialog()}
               name={selected.name}
               email={selected.email}
               user={selected.user}
               type={this.type.type}
               project={selected.project}
               team={selected.team}
               onChange={this.onChange}
               handleSubmit={this.handleSubmit}
               buttonCaption='Add'
               onClear={() => this.clearForm()}
               isAlertOpen={isAlertOpen}
               closeAlert={() => this.closeAlert()}
            />
            <Alert
               isOpen={this.state.isDeleteAlertOpen}
               confirmButtonText='Delete'
               onConfirm={() => this.deleteUser(selected._id)}
               cancelButtonText='Cancel'
               onCancel={() => this.closeDeleteAlert()}
               intent='danger'
               icon='issue'
               ussePortal={
                  <Portal container={document.getElementById('AdminPanel')} />
               }
            >
               <H5>Are you sure that you want to delete this user?</H5>
               <strong>{`User: ${selected.username}`}</strong>
               <p></p>
               <strong
                  className={Classes.TEXT_MUTED}
               >{`Will be permanentyly deleted...`}</strong>
            </Alert>
         </div>
      );
   }
}
