import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.css";
import "../../App.css";

export default class MainContainer extends Component {
   state = {
      theme: this.props.theme
   };

   setBgByTheme(theme) {
      if (theme === "dark") {
         return "main-data-bg-dark";
      }
      if (theme === "light") {
         return "main-data-bg-light";
      } else {
         return "main-data-bg-dark";
      }
   }

   render() {
      return (
         <div className={`App ${this.setBgByTheme(this.state.theme)}`}>
            <div className="main-data">
               {this.props.children}
            </div>
         </div>
      );
   }
}
