import React, { Component } from 'react'
import {
   Card, CardHeader, CardFooter, CardBody,
   CardTitle, CardText
} from 'reactstrap';
import { Row, Col } from 'reactstrap';

import 'bootstrap/dist/css/bootstrap.min.css'

export default class StdCard extends Component {
   render() {

      const { headerText, bodyText, cardText } = this.props;

      return (
         <Col sm="3" xs="3" mt="4">
            <Card color="dark" className="text-white mt-4">
               <CardHeader>
                  {headerText}
               </CardHeader>
               <CardBody>
                  {bodyText}
                  <CardText>
                     {cardText}
                  </CardText>
               </CardBody>
            </Card>
         </Col>
      )
   }
}
