import React, { Component } from 'react';
import { Card, CardHeader, CardBody, CardText } from 'reactstrap';
import { Col } from 'reactstrap';
import { Collapse } from 'reactstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './StdCard.css';

export default class StdCard extends Component {
   state = {
      theme: this.props.theme,
      collapse: true
   };

   setTheme(theme) {
      if (theme === 'dark') {
         return 'bg-dark text-white';
      }
      if (theme === 'light') {
         return 'bg-light';
      }
   }

   setSize(size) {
      if (size === 's')
         return (size = {
            xl: 4,
            lg: 4,
            md: 4,
            sm: 6,
            xs: 12
         });
      if (size === 'm')
         return (size = {
            xl: 6,
            lg: 6,
            md: 6,
            sm: 6,
            xs: 12
         });
      if (size === 'l')
         return (size = {
            xl: 8,
            lg: 8,
            md: 8,
            sm: 12,
            xs: 12
         });
      if (size === 'xl')
         return (size = {
            xl: 12,
            lg: 12,
            md: 12,
            sm: 12,
            xs: 12
         });
      return (size = {
         xl: 6,
         lg: 6,
         md: 6,
         sm: 6,
         xs: 12
      });
   }

   toggle = () => {
      this.setState(state => ({ collapse: !state.collapse }));
   };

   render() {
      const { icon, headerText, bodyText, cardText, size } = this.props;

      return (
         <Col
            xl={this.setSize(size).xl}
            lg={this.setSize(size).lg}
            md={this.setSize(size).md}
            sm={this.setSize(size).sm}
            xs={this.setSize(size).xs}
            className='mt-4'
         >
            <Card className={this.setTheme(this.props.theme)}>
               <CardHeader>
                  <span class={`${icon} header-icon float-left`} />

                  <span
                     class='fa fa-angle-down float-right header-icon text-secondary'
                     onClick={this.toggle}
                  />
                  {headerText}
               </CardHeader>
               <Collapse isOpen={this.state.collapse}>
                  <CardBody>
                     {bodyText}
                     {this.props.children}
                     <CardText>{cardText}</CardText>
                  </CardBody>
               </Collapse>
            </Card>
         </Col>
      );
   }
}
