import React, { Component } from 'react'

const { title, procValue, numberValue, bgColor} = this.props;

export default class ScoreCard extends Component {

   state = {
      name: 'N/A',
      proc: 0,
      number: 0,
      trend: 'fa-minus-circle',
      bgColor: 'bg-c-white',
      txtColor: 'text-gray'
   }

   render() {
      return (
         <div>
            <div className="col-sm-2 col-xs-6 mt-4" >
               <div className="card txt-grey">
                  <div className="card-body">
                     <h6>{title}
                           <span className="fas fa-minus-circle float-right">
                        </span>
                        <b class="float-right text-sm">{procValue}%</b>
                     </h6>
                     <h1>
                        <span className="fa fa-users"></span>
                        <span className="float-right text-big">{numberValue}</span>
                     </h1>
                  </div>
               </div>
            </div>
         </div>
      )
   }
}
