import React, { Component } from 'react';
import Chart from 'react-apexcharts';
import { Card } from '@blueprintjs/core';
import UIManager from '../../../Modules/UIManager/UIManager';

export default class LineChart extends Component {
   constructor(props) {
      super(props);

      this.state = {
         options: {
            chart: {
               id: 'basic-bar',
               background: 'transparent',
               dropShadow: {
                  enabled: true,
                  color: '#000',
                  top: 18,
                  left: 7,
                  blur: 10,
                  opacity: 0.2
               },
               toolbar: false
            },
            dataLabels: {
               enabled: true
            },
            stroke: {
               curve: 'smooth'
            },
            title: {
               text: 'Some Data',
               align: 'left'
            },
            grid: {
               borderColor: 'rbga(0,0,0,0.5)',
               row: {
                  colors: ['rgba(0,0,0,0.2)', 'transparent'], // takes an array which will be repeated on columns
                  opacity: 0.5
               }
            },
            xaxis: {
               type: 'category',
               title: {
                  text: 'Year'
               },
               categories: [
                  1991,
                  1992,
                  1993,
                  1994,
                  1995,
                  1996,
                  1997,
                  1998,
                  1999
               ],

               axisBorder: {
                  show: false
               },
               annotations: {
                  yaxis: [
                     {
                        y: 8200,
                        borderColor: '#00E396',
                        label: {
                           borderColor: '#00E396',
                           style: {
                              color: '#fff',
                              background: '#00E396'
                           },
                           text: 'Support'
                        }
                     },
                     {
                        y: 20,
                        y2: 50,
                        borderColor: '#000',
                        fillColor: '#FEB019',
                        opacity: 0.2,
                        label: {
                           borderColor: '#333',
                           style: {
                              fontSize: '10px',
                              color: '#333',
                              background: '#FEB019'
                           },
                           text: 'Y-axis range'
                        }
                     }
                  ],
                  points: [
                     {
                        x: 1995,
                        y: 49,
                        marker: {
                           size: 8,
                           fillColor: '#fff',
                           strokeColor: 'red',
                           radius: 2,
                           cssClass: 'apexcharts-custom-class'
                        },
                        label: {
                           borderColor: '#FF4560',
                           offsetY: 0,
                           style: {
                              color: '#fff',
                              background: '#FF4560'
                           },

                           text: 'Point Annotation'
                        }
                     }
                  ]
               },
               tooltip: {
                  enabled: false,
                  formatter: undefined,
                  offsetY: 0,
                  style: {
                     fontSize: 0,
                     fontFamily: 0
                  }
               }
            },
            yaxis: {
               title: {
                  text: 'Values'
               },
               min: 5,
               max: 150
            },
            legend: {
               position: 'top',
               horizontalAlign: 'right',
               floating: true,
               offsetY: -25,
               offsetX: -5
            }
         },
         series: [
            {
               name: 'series-1',
               data: [30, 40, 45, 50, 49, 60, 70, 91]
            },
            {
               name: 'series-2',
               data: [10, 20, 30, 100, 120, 20, 10, 5]
            }
         ]
      };
   }
   render() {
      return (
         <Card>
            <div className='card-body text'>
               <Chart
                  options={this.state.options}
                  series={this.state.series}
                  type='line'
                  width='100%'
               />
            </div>
         </Card>
      );
   }
}
