import React, { Component } from 'react';
import { Card, Tag } from '@blueprintjs/core';
import '../QuickView/QuickView.css';
import './ScoreCard.css';

import { Col, CardBody, CardText, Badge } from 'reactstrap';

/*TODO:
   -- fix title / badge relation
      -- overlaping title over badge
      -- hight issue on mid horizontal size
      -- rounding corners! 10px
   FIXME:
   -- state mutation warrning!
*/
export default class ScoreCard extends Component {
   state = {
      name: 'N/A',
      icon: '',
      proc: 0,
      number: 0,
      bgColor: 'bg-c-white',
      txtColor: 'text-gray'
   };

   getTrend = value => {
      if (value < 0) return '-';
      else if (value > 0) return '+';
      else return ' ';
   };

   getBgColor = trend => {
      this.state.txtColor = 'text-white';
      if (trend < 0) return 'bg-danger';
      else if (trend > 0) return 'bg-success ';
      else {
         this.state.txtColor = 'text-black';
         return '';
      }
   };

   getIcon = iconName => {
      if (!iconName) return 'fas fa-bug';
      else return iconName;
   };

   render() {
      const { title, icon, procValue, numberValue, trend } = this.props;

      return (
         <Col xl='2' lg='3' md='3' sm='6' xs='12' className='mt-4'>
            <Card className='mb-0 score-card'>
               <div className='mt-2 mr-3 ml-3'>
                  {title}
                  <Tag className='float-right' icon='plus'></Tag>

                  <Badge
                     className={`${this.getBgColor(trend)} ${
                        this.state.txtColor
                     }ml-auto float-right`}
                  >
                     {this.getTrend(this.props.trend) + procValue}%
                  </Badge>
               </div>
               <CardBody>
                  <CardText>
                     <h1>
                        <span
                           className={`${this.getIcon(
                              icon
                           )} text-secondary icon-big`}
                        />
                        <span className='float-right text-mid mr-0 score-numbers'>
                           {numberValue}
                        </span>
                     </h1>
                  </CardText>
               </CardBody>
            </Card>
         </Col>
      );
   }
}
