import React, { Component } from 'react';
import { Input, FormText } from 'reactstrap';
import axios from 'axios';

export default class LanguageOptions extends Component {
   state = {
      language: '',
      languageArr: []
   };

   componentDidMount() {
      this.getOptions();
   }

   getOptions() {
      const URL = '/api/languages';
      axios
         .get(URL)
         .then(res => {
            this.setState({
               language: res.data[0]._id,
               languageArr: res.data
            });
         })
         .catch(err => {
            alert('Could not load languages');
            console.log(JSON.stringify(err));
         });
   }

   renderOptions() {
      return this.state.languageArr.map((option, key) => {
         return (
            <option key={key} value={option._id}>
               {option.language}
            </option>
         );
      });
   }

   render() {
      return (
         <div>
            <Input
               type='select'
               name={this.props.name}
               id={this.props.name}
               ref={this.props.name}
               size='sm'
               onChange={this.props.onChange}
               className={this.props.className}
               disabled={this.props.disabled}
            >
               <option selected hidden disabled>
                  Please Select Language
               </option>
               {this.renderOptions()}
            </Input>
            <FormText>Please provide main language</FormText>
         </div>
      );
   }
}
