import React, { Component } from 'react';
import { Menu, MenuItem } from '@blueprintjs/core';

export default class TableContextMenu extends Component {
   render() {
      const { role, onEdit, onRemove, isEligable } = this.props;
      return (
         <Menu>
            <MenuItem icon='form' text={`${role.role} `} disabled />
            <Menu.Divider />
            <div hidden={isEligable}>
               <MenuItem
                  icon='edit'
                  text='Edit role'
                  onClick={onEdit}
                  disabled={isEligable}
               />
               <MenuItem
                  icon='remove'
                  text='Remove role'
                  onClick={onRemove}
                  disabled={isEligable}
               />
            </div>
            <MenuItem icon='properties' text='Show more...' />
         </Menu>
      );
   }
}
