import React, { Component } from "react";
import {
   Row,
   Col,
   Card,
   CardBody,
   Button,
   Form,
   FormGroup,
   Input,
   InputGroup,
   InputGroupAddon,
   InputGroupText
} from "reactstrap";
import Auth from "../Modules/Auth/Auth";

import "./Login.css";
import "bootstrap/dist/css/bootstrap.css";
import "@fortawesome/fontawesome-free/css/all.css";

export default class Login extends Component {
   state = {
      buttonColor: "",
      isLoading: false,
      email: "",
      password: ""
   };

   handleSubmit = async event => {
      event.preventDefault();
      try {
         alert("submit");
         alert(this.state.email);
      } catch (e) {
         alert(e.message);
      }
   };

   updateButtonColor() {
      if (!this.state.email & !this.state.password)
         this.setState({ buttonColor: "primary" });
   }

   updateInputValue(evt) {
      this.setState({
         email: evt.target.value
      });
      console.log(evt.target.value);
      this.updateButtonColor();
   }

   render() {
      return (
         <div className="login-page">
            <Row>
               <Col
                  xl={{ size: 4, offset: 4 }}
                  lg={{ size: 4, offset: 4 }}
                  md={{ size: 4, offset: 4 }}
                  sm="12"
                  xs="12"
                  className="mt-4"
               >
                  <Card inverse color="dark" className="bg-login-form">
                     <CardBody className="bg-login-form">
                        <Form>
                           <span className="fas fa-dice-d20 float-right" />
                           <p className="text-center">Sign in</p>
                           <FormGroup>
                              <InputGroup size="sm">
                                 <InputGroupAddon addonType="prepend">
                                    <InputGroupText>
                                       <i className="fas fa-user" />
                                    </InputGroupText>
                                 </InputGroupAddon>
                                 <Input
                                    type="email"
                                    name="email"
                                    id="exampleEmail"
                                    placeholder="email"
                                    onChange={evt => this.updateInputValue(evt)}
                                 />
                              </InputGroup>
                           </FormGroup>
                           <FormGroup>
                              <InputGroup size="sm">
                                 <InputGroupAddon addonType="prepend">
                                    <InputGroupText>
                                       <i className="fas fa-key" />
                                    </InputGroupText>
                                 </InputGroupAddon>
                                 <Input
                                    color={this.state.buttonColor}
                                    type="password"
                                    name="password"
                                    id="examplePassword"
                                    bsSize="sm"
                                    placeholder="password"
                                    onChange={evt => this.updateInputValue(evt)}
                                 />
                              </InputGroup>
                           </FormGroup>
                           <Button
                              type="submit"
                              className="float-right"
                              size="sm"
                           >
                              Login
                           </Button>
                        </Form>
                     </CardBody>
                  </Card>
               </Col>
            </Row>
         </div>
      );
   }
}
