import React, { Component } from 'react';
import { Row, Col } from 'reactstrap';
import CreateuserForm from './components/CreateUserForm';
import Client from '../../Modules/Client/Client';

export default class AdminPanel extends Component {
   state = {
      toasts: [],
      username: '',
      email: '',
      password: '',
      role: 'USER',
      message: '',
      saved: ''
   };

   onChange = e => {
      const state = this.state;
      state[e.target.id] = e.target.value;
      this.setState(state);
   };
   handleSubmit = async e => {
      const { email, username, role, password, message } = this.state;
      e.preventDefault();
      //FIXME: backend issue
      let result = null;

      let usr = Client.Services.UsersService.createUserDTO(
         username,
         email,
         password,
         role,
         '5dfffe289fe19f21c85f3287', //<- temporaryfix
         '5dfffe289fe19f21c85f3287' //<- temporaryfix
      );
      result = await Client.Services.UsersService.saveNewUser(usr)
         .then(res => {
            this.setState({ message: 'User created successfully' });
            return res;
         })
         .catch(err => {
            return err;
         });

      if (result.status === 200) {
         Client.Notifications.showSuccessNotification(
            `New user ${username} was successfully created`
         );
      }
      if (message) {
         Client.Notifications.showAlertNotification(
            `Errow while saving user ${username} 
            Error status code: ${result.status}
            `
         );
      }
   };

   render() {
      const { email, username, role, password } = this.state;
      return (
         <div>
            <Row>
               <Col sx='12' sm='6'>
                  <CreateuserForm
                     email={email}
                     username={username}
                     password={password}
                     role={role}
                     onChange={this.onChange}
                     onClick={this.handleSubmit}
                  />
               </Col>
            </Row>
         </div>
      );
   }
}
