import React, { Component } from 'react';
import { Row } from 'reactstrap';
import { Button } from '@blueprintjs/core';
import MainContainer from '../../components/MainContainer/MainContainer';
import IconCard from './components/IconCard/IconCard';
import StdCard from '../../components/Card/StdCard';
import Client from '../../Modules/Client/Client';

import UIManager from '../../Modules/UIManager/UIManager';

export default class Home extends Component {
   constructor(props) {
      super(props);
      this.state = {
         theme: UIManager.getTheme(),
         testResult: null,
         testServiceResult: []
      };
      this.getUserData = this.getUserData.bind(this);
      this.testService = this.testService.bind(this);
   }

   async getUserData() {
      let result = await Client.testingCurrentUserAuth();
      this.setState({
         testResult: result
      });
   }

   async testService() {
      let _r = await Client.Services.EmployeesService.getEmployees();
      this.setState({
         testServiceResult: `${_r[0].name}  ${_r[0].email} \n ${_r[0]._id}`
      });
   }

   render() {
      return (
         <MainContainer>
            <div className='container-fluid'>
               <Row>
                  <IconCard
                     icon='chart'
                     theme={this.state.theme}
                     title='Dashboards'
                     link='/Dashboards/'
                  />
                  <IconCard
                     icon='projects'
                     theme={this.state.theme}
                     title='Projects'
                     link='/Project/'
                  />
                  <IconCard
                     icon='join-table'
                     theme={this.state.theme}
                     title='Report'
                     link='/Report/'
                  />
                  <IconCard
                     icon='cog'
                     theme={this.state.theme}
                     title='Admin Panel'
                     link='AdminPanel'
                  />
               </Row>
               <Row>
                  <StdCard
                     icon='information'
                     headerText='Project info'
                     size='xl'
                  >
                     <h3> Project info </h3>
                     <hr />
                     <h5>current version: 0.0.1</h5>
                     <hr />
                     <h5>Author: Rafal Cymbalista</h5>

                     <h5>
                        Test Authentication:{' '}
                        <Button
                           icon={this.state.testResult ? 'unlock' : 'lock'}
                           minimal
                           onClick={this.getUserData}
                        ></Button>
                        <p>{this.state.testResult}</p>
                     </h5>
                  </StdCard>
                  <StdCard headerText='test.serv' icon='play'>
                     <h3>
                        Testing Services{' '}
                        <Button
                           icon='play'
                           minimal
                           intent='primary'
                           onClick={this.testService}
                        />
                     </h3>

                     <p>{this.state.testServiceResult}</p>
                  </StdCard>
               </Row>
            </div>
         </MainContainer>
      );
   }
}
