import React, { Component } from 'react';
import {
   Row,
   Col,
   Alert,
   Button,
   Form,
   FormGroup,
   Label,
   Input
} from 'reactstrap';
import axios from 'axios';
import UIManager from '../../../Modules/UIManager/UIManager';
import StdCard from '../StdCard';
import ProjectTable from '../../Table/ProjectTable/ProjectTable';

export default class ProjectCard extends Component {
   constructor(props) {
      super(props);
      this.state = {
         theme: UIManager.getTheme(),
         projectName: ''
      };
   }

   onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
   };

   setInputTheme() {
      const { theme } = this.props;
      if (theme === 'dark') {
         return 'table-input-dark';
      }
      if (theme === 'light') {
         return '';
      }
   }
   setSelectTheme() {
      const { theme } = this.props;
      if (theme === 'dark') {
         return 'my-select-dark';
      }
      if (theme === 'light') {
         return '';
      }
   }

   render() {
      const { theme, projectName } = this.state;

      return (
         <StdCard
            theme={theme}
            icon='fas fa-project-diagram'
            headerText='Projects'
            size='xl'
         >
            <div>
               <h5>Project info</h5>
            </div>
            <div>
               <Form>
                  <FormGroup row>
                     <Label for='projectName' md={2}>
                        Find Project
                     </Label>
                     <Col md={7}>
                        <Input
                           type='input'
                           name='projectName'
                           id='projectName'
                           size='sm'
                           value={projectName}
                           onChange={this.onChange}
                           placeholder='Please provide Project Name'
                           className={this.setSelectTheme()}
                        />
                     </Col>
                     <Col md={2}>
                        <Button color='primary' size='sm'>
                           Find
                        </Button>
                     </Col>
                  </FormGroup>
               </Form>
            </div>

            <ProjectTable theme={theme} name={projectName} />
         </StdCard>
      );
   }
}
