import React, { Component } from 'react';
import {
   Col,
   Alert,
   Button,
   Form,
   FormGroup,
   FormText,
   Label,
   Input,
   ButtonGroup
} from 'reactstrap';
import axios from 'axios';
import ProjectStatusOptions from '../../Options/ProjectStatusOptions';
import ProjectDeliveryTypeOptions from '../../Options/ProjectDeliveryTypeOptions';
import SMOptions from '../../Options/SMOptions';
import OPSOptions from '../../Options/OPSOptions';

export default class CreateProjectForm extends Component {
   state = {
      theme: this.props.theme,
      projectName: '',
      status: '',
      deliveryType: '',
      sm: '',
      ops: '',
      cim: 0,
      ras: 0,
      message: ''
   };

   onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
   };

   onSubmit = e => {
      e.preventDefault();

      const {
         projectName,
         status,
         deliveryType,
         sm,
         ops,
         cim,
         ras
      } = this.state;

      const URL = '/api/projects/';

      const data = {
         project: {
            name: projectName,
            status: status,
            deliveryType: deliveryType,
            sm: sm,
            ops: ops,
            cim: cim,
            ras: ras,
            created: ''
         }
      };

      axios
         .post(URL, data)
         .then(res => {
            this.setState({
               created: 'ok',
               message: 'Project saved successfully'
            });
         })
         .catch(err => {
            this.setState({
               created: 'error',
               message: 'Something went wrong',
               err: err
            });
         });
   };

   clearForm = () => {
      this.setState({
         projectName: '',
         status: 'Delivery',
         deliveryType: 'Dedicated',
         sm: '',
         ops: '',
         cim: '',
         ras: '',
         message: ''
      });
   };

   setInputTheme() {
      const { theme } = this.props;
      if (theme === 'dark') {
         return 'bg-secondary text-white no-border';
      }
      if (theme === 'light') {
         return '';
      }
   }

   setSelectTheme() {
      const { theme } = this.props;
      if (theme === 'dark') {
         return 'bg-secondary text-white no-border';
      }
      if (theme === 'light') {
         return '';
      }
   }

   alertStyle() {
      const { created } = this.state;
      if (created === 'ok') return 'border-success text-success';
      if (created === 'error') return 'border-danger text-danger';
   }

   render() {
      const {
         projectName,
         status,
         deliveryType,
         sm,
         ops,
         cim,
         ras,
         message
      } = this.state;

      return (
         <div>
            {message !== '' && (
               <Alert
                  className={`bg-transparent border rounded-corners text-center ${this.alertStyle()}`}
               >
                  {message}
               </Alert>
            )}
            <Form className='mb-2' ref='createProjectForm'>
               <FormGroup row>
                  <Label
                     className='text-right'
                     for='projectName'
                     md={3}
                     size='sm'
                  >
                     Project Name
                  </Label>
                  <Col md={9}>
                     <Input
                        type='input'
                        name='projectName'
                        id='projectName'
                        ref='projectName'
                        size='sm'
                        value={projectName}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setInputTheme()}
                     />
                     <FormText>Please provide Project Name</FormText>
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='status' md={3} size='sm'>
                     Status
                  </Label>
                  <Col md={3}>
                     <ProjectStatusOptions
                        name='status'
                        value={status}
                        onChange={this.onChange}
                        className={this.setSelectTheme()}
                     />
                  </Col>

                  <Label
                     className='text-right'
                     for='deliveryType'
                     md={3}
                     size='sm'
                  >
                     Delivery Type
                  </Label>
                  <Col md={3}>
                     <ProjectDeliveryTypeOptions
                        name='deliveryType'
                        value={deliveryType}
                        onChange={this.onChange}
                        className={this.setSelectTheme()}
                     />
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='sm' md={3} size='sm'>
                     Service Manager
                  </Label>
                  <Col md={9}>
                     <SMOptions
                        name='sm'
                        value={sm}
                        onChange={this.onChange}
                        className={this.setSelectTheme()}
                     />
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Label className='text-right' for='ops' md={3} size='sm'>
                     Operations Manager
                  </Label>
                  <Col md={9}>
                     <OPSOptions
                        name='ops'
                        value={ops}
                        onChange={this.onChange}
                        className={this.setSelectTheme()}
                     />
                  </Col>
               </FormGroup>
               <FormGroup row>
                  <Col md={4} />
                  <Label className='text-right' for='cim' md={1} size='sm'>
                     CIM
                  </Label>
                  <Col md={3}>
                     <Input
                        type='number'
                        name='cim'
                        id='cim'
                        size='sm'
                        value={cim}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setSelectTheme()}
                     />
                     <FormText>Please provide CIM</FormText>
                  </Col>

                  <Label className='text-right' for='ras' md={1} size='sm'>
                     RAS
                  </Label>
                  <Col md={3}>
                     <Input
                        type='number'
                        name='ras'
                        id='ras'
                        size='sm'
                        value={ras}
                        onChange={this.onChange}
                        placeholder=''
                        className={this.setSelectTheme()}
                     />
                     <FormText>Please provide RAS</FormText>
                  </Col>
               </FormGroup>

               <div className='text-right'>
                  <ButtonGroup>
                     <Button size='sm' onClick={this.clearForm}>
                        Clear
                     </Button>
                     <Button color='primary' size='sm' onClick={this.onSubmit}>
                        Add
                     </Button>
                  </ButtonGroup>
               </div>
            </Form>
         </div>
      );
   }
}
