import React, { Component } from "react";
import {
   Row,
   Col,
   Card,
   CardBody,
   Button,
   Form,
   FormGroup,
   Input,
   InputGroup,
   InputGroupAddon,
   InputGroupText
} from "reactstrap";
import Auth from "../Modules/Auth/Auth";
import {
   BrowserRouter as Router,
   Route,
   Link,
   Redirect
} from "react-router-dom";
import axios from "axios";
import MainContainer from "../components/MainContainer/MainContainer";

import "./Login.css";
import "bootstrap/dist/css/bootstrap.css";
import "@fortawesome/fontawesome-free/css/all.css";

export default class Login extends Component {
   state = {
      theme: "dark",
      buttonColor: "secondary",
      isLoading: false,
      authenticated: false,
      email: "",
      password: ""
   };

   onSubmit = e => {
      e.preventDefault();

      const { username, password } = this.state;

      axios
         .post("/api/auth/login", {
            username,
            password
         })
         .then(result => {
            localStorage.setItem("jwtToken", result.data.token);
            this.setState({ message: "" });
            this.props.history.push("/");
         })
         .catch(error => {
            if (error.response.status === 401) {
               this.setState({
                  message: "Login failed. Username or password not match"
               });
            }
         });
   };

   onChange = e => {
      const state = this.state;
      state[e.target.name] = e.target.value;
      this.setState(state);
   };

   updateEmailValue(evt) {
      this.setState({
         email: evt.target.value
      });
   }
   updatePasswordValue(evt) {
      this.setState({
         password: evt.target.value
      });
   }

   isAuthenticated() {
      if (this.state.authenticated) {
         return <Redirect to="/" />;
      }
   }

   render() {
      const { username, password, message } = this.state;

      return (
         <MainContainer theme="dark">
            <Row>
               <Col
                  xl={{ size: 4, offset: 4 }}
                  lg={{ size: 4, offset: 4 }}
                  md={{ size: 4, offset: 4 }}
                  sm="12"
                  xs="12"
                  className="mt-4"
               >
                  <Card inverse color="dark" className="bg-login-form mt-5">
                     <CardBody className="bg-login-form">
                        <Form>
                           {message !== "" && (
                              <div
                                 className="alert alert-warning alert-dismissible"
                                 role="alert"
                              >
                                 {message}
                              </div>
                           )}
                           <span className="fas fa-dice-d20 float-right" />
                           <p className="text-center">Sign in</p>
                           <FormGroup>
                              <InputGroup size="sm">
                                 <InputGroupAddon addonType="prepend">
                                    <InputGroupText>
                                       <i className="fas fa-user" />
                                    </InputGroupText>
                                 </InputGroupAddon>
                                 <Input
                                    type="email"
                                    name="email"
                                    id="exampleEmail"
                                    placeholder="email"
                                    bsSize="sm"
                                    value={username}
                                    onChange={this.onChange}
                                 />
                              </InputGroup>
                           </FormGroup>
                           <FormGroup>
                              <InputGroup size="sm">
                                 <InputGroupAddon addonType="prepend">
                                    <InputGroupText>
                                       <i className="fas fa-key" />
                                    </InputGroupText>
                                 </InputGroupAddon>
                                 <Input
                                    color={this.state.buttonColor}
                                    type="password"
                                    name="password"
                                    id="password"
                                    placeholder="password"
                                    bsSize="sm"
                                    value={password}
                                    onChange={this.onChange}
                                 />
                              </InputGroup>
                           </FormGroup>
                           <Button
                              type="submit"
                              className="float-right"
                              size="sm"
                              onClick={this.onSubmit()}
                           >
                              Login
                           </Button>
                        </Form>
                     </CardBody>
                  </Card>
               </Col>
            </Row>
         </MainContainer>
      );
   }
}
